INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (310, 6309, 'Levodopa', 'Alcohol may potentiate some of the pharmacologic effects of levodopa.  Use in combination may result in additive central nervous system depression and/or impairment of judgment, thinking, and psychomotor skills.', 'alcohol', 'Moderate', 'In general, alcohol consumption should be avoided or limited during treatment with CNS-depressant agents.  Until more data are available, it is advisable to avoid large fluctuations in daily protein intake and to monitor patients for altered effects of levodopa if the protein content of the diet is increased.', NULL, NULL, 'DDInter', 0);
