INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (536, 8332, 'Apomorphine', 'Alcohol and apomorphine may have additive hypotensive and sedative effects.  Coadministration of 0.6 or 0.3 g/kg of ethanol with apomorphine in healthy subjects resulted in greater decreases in blood pressure compared to apomorphine alone.', 'alcohol', 'Moderate', 'Patients should be advised to avoid consumption of alcohol during apomorphine treatment.', NULL, NULL, 'DDInter', 0);
