INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (539, 17400, 'Lofexidine', 'Alcohol may potentiate some of the pharmacologic effects of CNS-active agents.  Use in combination may result in additive central nervous system depression and/or impairment of judgment, thinking, and psychomotor skills.', 'alcohol', 'Moderate', 'Patients receiving CNS-active agents should be warned of this interaction and advised to avoid or limit consumption of alcohol.  Ambulatory patients should be counseled to avoid hazardous activities requiring complete mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', NULL, NULL, 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (540, 7568, 'Lovastatin', 'Concomitant use of statin medication with substantial quantities of alcohol may increase the risk of hepatic injury.  Transient increases in serum transaminases have been reported with statin use and while these increases generally resolve or improve with continued therapy or a brief interruption in therapy, there have been rare postmarketing reports of fatal and non-fatal hepatic failure in patients taking statins.  Patients who consume substantial quantities of alcohol and/or have a history of liver disease may be at increased risk for hepatic injury.  Active liver disease or unexplained transaminase elevations are contraindications to statin use.  ', 'alcohol', 'Moderate', 'Patients should be counseled to avoid substantial quantities of alcohol in combination with statin medications and clinicians should be aware of the increased risk for hepatotoxicity in these patients.', NULL, NULL, 'DDInter', 0);
