INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (618, 0, 'Macimorelin', 'Food reduces the oral bioavailability of macimorelin.  According to the product labeling, administration with a liquid meal decreased macimorelin peak plasma concentration (Cmax) and systemic exposure (AUC) by 55% and 49%, respectively, compared to administration under fasting conditions (i.e., for at least 8 hours).', 'food', 'Moderate', 'Macimorelin should be administered after fasting for at least 8 hours.', NULL, NULL, 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (619, 0, 'Maraviroc', 'Administration with food may reduce the bioavailability of maraviroc.  According to the product labeling, coadministration of a 300 mg dose of maraviroc with a high-fat breakfast reduced maraviroc peak plasma concentration (Cmax) and systemic exposure (AUC) by 33% in healthy volunteers.', 'high-fat foods', 'Minor', 'No food restrictions were used in the clinical studies that demonstrated the safety and efficacy of maraviroc.  Therefore, maraviroc can be taken with or without food at the recommended dosage.', NULL, NULL, 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (620, 0, 'Maprotiline', 'Alcohol may potentiate some of the pharmacologic effects of CNS-active agents.  Use in combination may result in additive central nervous system depression and/or impairment of judgment, thinking, and psychomotor skills.', 'alcohol', 'Moderate', 'Patients receiving CNS-active agents should be warned of this interaction and advised to avoid or limit consumption of alcohol.  Ambulatory patients should be counseled to avoid hazardous activities requiring complete mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', NULL, NULL, 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (621, 8617, 'Meclizine', 'Use of anticholinergic agents with alcohol may result in sufficient impairment of attention so as to render driving and operating machinery more hazardous.  In addition, the potential for abuse may be increased with the combination.  The mechanism of interaction is not established but may involve additive depressant effects on the central nervous system.', 'alcohol', 'Moderate', 'Alcohol should generally be avoided during therapy with anticholinergic agents.  Patients should be counseled to avoid activities requiring mental alertness until they know how these agents affect them.', NULL, NULL, 'DDInter', 0);
