INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (747, 0, 'Methadone', 'Ethanol may potentiate the central nervous system (CNS) depressant effects of opioid analgesics.  Concomitant use may result in additive CNS depression and impairment of judgment, thinking, and psychomotor skills.  In more severe cases, hypotension, respiratory depression, profound sedation, coma, or even death may occur.', 'alcohol', 'Moderate', 'Concomitant use of opioid analgesics with ethanol should be avoided.', NULL, NULL, 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (748, 0, 'Methadone', 'Coadministration with grapefruit juice may increase the plasma concentrations of methadone.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall by certain compounds present in grapefruits.', 'grapefruit juice', 'Moderate', 'Given the interindividual variability in the pharmacokinetics of methadone, a more significant interaction with grapefruit juice in certain patients cannot be ruled out.  Patients treated with methadone should preferably avoid or limit the consumption of grapefruit juice, particularly during the induction of maintenance treatment.', NULL, NULL, 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (749, 0, 'Methohexital', 'Concurrent acute use of barbiturates and ethanol may result in additive CNS effects,  including impaired coordination, sedation, and death.  Tolerance of these agents may occur with chronic use.  The mechanism is related to inhibition of microsomal enzymes acutely and induction of hepatic microsomal enzymes chronically.', 'alcohol', 'Major', 'The combination of ethanol and barbiturates should be avoided.', NULL, NULL, 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (750, 8642, 'Methoxsalen', 'The ingestion of foods containing photosensitizing components (e.g., limes, figs, parsley, parsnips, rue (Ruta graveolens), mustard, carrots and celery) may increase the risk of photosensitivity and severe burning during methoxsalen therapy.', 'foods containing photosensitizing components', 'Moderate', 'Patients who are undergoing PUVA treatment and taking methoxsalen should be advised to avoid eating large quantities of these foods.', NULL, NULL, 'DDInter', 0);
