INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (786, 5019, 'Methotrexate', 'Limited data suggest that consumption of greater than 180 mg/day of caffeine may interfere with the efficacy of methotrexate (MTX) in patients with rheumatoid arthritis.  The exact mechanism of interaction is unknown but may be related to the antagonistic effect of caffeine on adenosine receptors, as anti-inflammatory properties of MTX is thought to result from the accumulation of adenosine.', 'coffee', 'Moderate', 'Until further information is available, the potential for interaction should be considered in patients who consume substantial amounts of caffeine and caffeine-containing foods and are prescribed methotrexate for rheumatoid arthritis.  It may be appropriate to limit caffeine intake if an interaction is suspected in cases of treatment failure.', NULL, NULL, 'DDInter', 0);
