INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (903, 721, 'Methylnaltrexone', 'Food may reduce the rate and extent of absorption of methylnaltrexone following oral administration.  When a single 450 mg oral dose of methylnaltrexone was administered with a high-fat breakfast (approximately 800 to 1000 calories; 60% from fat, 25% from carbohydrate, and 15% from protein) in healthy study subjects, methylnaltrexone peak plasma concentration (Cmax) and systemic exposure (AUC) decreased by 60% and 43%, respectively, while time to reach Cmax delayed by 2 hours.', 'high-fat foods', 'Moderate', 'Oral methylnaltrexone should be taken with water on an empty stomach at least 30 minutes before the first meal of the day.', NULL, NULL, 'DDInter', 0);
