INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (915, 7520, 'Metoprolol', 'The bioavailability of metoprolol may be enhanced by food.', 'food', 'Moderate', 'Patients may be instructed to take metoprolol at the same time each day, preferably with or immediately following meals.', NULL, NULL, 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (916, 690, 'Metronidazole (topical)', 'Use of alcohol or products containing alcohol during nitroimidazole therapy may result in a disulfiram-like reaction in some patients.  There have been a few case reports involving metronidazole, although data overall are not convincing.  The presumed mechanism is inhibition of aldehyde dehydrogenase (ALDH) by metronidazole in a manner similar to disulfiram.  Following ingestion of alcohol, inhibition of ALDH results in increased concentrations of acetaldehyde, the accumulation of which can produce an unpleasant physiologic response referred to as the ''disulfiram reaction''.', 'alcohol', 'Moderate', 'Because clear evidence is lacking concerning the safety of ethanol use during nitroimidazole therapy, patients should be apprised of the potential for interaction and instructed to avoid alcoholic beverages and products containing alcohol or propylene glycol while using oral, intravenous, or vaginal preparations of a nitroimidazole.  Alcoholic beverages should not be consumed for up to 3 days after completion of systemic nitroimidazole therapy.', NULL, NULL, 'DDInter', 0);
