INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (2185, 31167, 'Ranolazine', 'Grapefruit and grapefruit juice may significantly increase the plasma concentrations of orally administered ranolazine.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall by certain compounds present in grapefruit.  Because ranolazine prolongs QT interval in a dose-dependent manner, high plasma levels of ranolazine may increase the risk of ventricular arrhythmias such as ventricular tachycardia, ventricular fibrillation, and torsade de pointes.', 'grapefruit juice', 'Major', 'Patients treated with ranolazine should avoid consumption of grapefruit juice and other grapefruit products if possible.  Otherwise, the dosage of ranolazine should be limited to 500 mg twice a day.', NULL, NULL, 'DDInter', 0);
