INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (2202, 0, 'Regadenoson', 'Caffeine and other xanthine derivatives are nonspecific, competitive antagonists of adenosine receptors.  As such, they may interfere with the vasodilating effect of regadenoson, an adenosine receptor agonist.', 'coffee', 'Moderate', 'Patients should avoid consumption of caffeine-containing products for at least 12 hours prior to administration of regadenoson for myocardial perfusion imaging.', NULL, NULL, 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (2203, 28656, 'Remifentanil', 'Ethanol may potentiate the central nervous system (CNS) depressant effects of opioid analgesics.  Concomitant use may result in additive CNS depression and impairment of judgment, thinking, and psychomotor skills.  In more severe cases, hypotension, respiratory depression, profound sedation, coma, or even death may occur.', 'alcohol', 'Moderate', 'Concomitant use of opioid analgesics with ethanol should be avoided.', NULL, NULL, 'DDInter', 0);
