INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (2275, 21431, 'Ribociclib', 'Pomegranates and grapefruit may increase the systemic exposure to ribociclib.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall by certain compounds present in these fruits.', 'grapefruit juice', 'Moderate', 'Patients receiving ribociclib should avoid consumption of pomegranates or pomegranate juice and grapefruit or grapefruit juice during treatment.', NULL, NULL, 'DDInter', 0);
