INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (2279, 4364, 'Rimegepant', 'Coadministration with grapefruit or grapefruit juice may increase the plasma concentrations of rimegepant.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall induced by certain compounds present in grapefruit.', 'grapefruit juice', 'Moderate', 'Rimegepant may be administered with or without food.  Until more information is available, patients receiving rimegepant may want to avoid the regular consumption of grapefruits and grapefruit juice to prevent undue increases in plasma levels and systemic effects of rimegepant.  If grapefruit or grapefruit juice is consumed concomitantly with rimegepant, the manufacturer recommends avoiding another dose of rimegepant within 48 hours.', NULL, NULL, 'DDInter', 0);
