INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (2822, 0, 'Stiripentol', 'Taking stiripentol on an empty stomach may reduce its oral bioavailability.  Stiripentol degrades rapidly when exposed to gastric acid in an empty stomach.', 'dairy products', 'Moderate', 'Stiripentol should be taken during a meal for optimal absorption; however, it should not be taken with milk, dairy products (e.g., yogurt, soft cream cheese), fruit juice, or carbonated beverages.  Patients should be advised to avoid or limit consumption of alcohol and to avoid activities requiring mental alertness such as driving or operating hazardous machinery until they know how the medication affects them.  Food and beverages that may contain caffeine or theophylline such as colas, chocolate, coffee, tea, or energy drinks should also be avoided during treatment with stiripentol.', NULL, NULL, 'DDInter', 0);
