INSERT OR IGNORE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (2538, 0, 'Saquinavir', 'Food significantly increases the absorption of saquinavir.', 'food', 'Moderate', 'Saquinavir mesylate should be taken with meals or within 2 hours after eating to enhance bioavailability.  Patients should be advised to avoid the consumption of large amounts of grapefruit and grapefruit juice during saquinavir therapy unless otherwise directed by their doctor, as the interaction is unreliable and subject to a high degree of interpatient variation.', NULL, NULL, 'DDInter', 0);
