INSERT OR IGNORE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (2552, 0, 'Secobarbital', 'Concurrent acute use of barbiturates and ethanol may result in additive CNS effects,  including impaired coordination, sedation, and death.  Tolerance of these agents may occur with chronic use.  The mechanism is related to inhibition of microsomal enzymes acutely and induction of hepatic microsomal enzymes chronically.', 'alcohol', 'Major', 'The combination of ethanol and barbiturates should be avoided.', NULL, NULL, 'DDInter', 0);
INSERT OR IGNORE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (2553, 3647, 'Sibutramine', 'Alcohol may potentiate the central nervous system and cardiovascular effects of centrally-acting appetite suppressants.', 'alcohol', 'Moderate', 'Concomitant use of centrally-acting appetite suppressants and alcohol should be avoided if possible, especially in patients with a history of cardiovascular disease.  Patients should be counselled to avoid hazardous activities requiring complete mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', NULL, NULL, 'DDInter', 0);
INSERT OR IGNORE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (2554, 5732, 'Sertraline', 'Limited clinical data suggest that consumption of grapefruit juice during treatment with sertraline may result in increased plasma concentrations of sertraline.  The proposed mechanism is inhibition of CYP450 3A4-mediated metabolism by certain compounds present in grapefruit.', 'grapefruit juice', 'Moderate', 'Patients receiving sertraline should be advised to avoid or limit consumption of alcohol.  Ambulatory patients should be counseled to avoid hazardous activities requiring complete mental alertness and motor coordination until they know how sertraline affects them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.  Some authorities recommend that consumption of grapefruit juice should be avoided during sertraline therapy.', NULL, NULL, 'DDInter', 0);
