INSERT OR IGNORE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (2725, 24039, 'Simeprevir', 'Food significantly enhances the oral bioavailability of simeprevir, although the type of food does not seem to matter.  In healthy study subjects, administration of simeprevir after a high-fat, high-caloric (928 kcal) breakfast increased systemic exposure (AUC) by 61% and delayed absorption by 1 hour, while administration after a normal caloric (533 kcal) breakfast increased AUC by 69% and delayed absorption by 1.5 hours.', 'high-fat foods', 'Moderate', 'To ensure maximal oral absorption, simeprevir should be administered with food.', NULL, NULL, 'DDInter', 0);
