INSERT OR IGNORE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (2935, 4949, 'Sunitinib', 'Consumption of grapefruit or grapefruit juice during sunitinib therapy may increase the plasma concentrations of sunitinib.  The proposed mechanism is inhibition of CYP450 3A4-mediated metabolism by certain compounds present in grapefruit.', 'grapefruit juice', 'Moderate', 'Although clinical data are lacking, it may be advisable to avoid the consumption of large amounts of grapefruit or grapefruit juice during sunitinib therapy.', NULL, NULL, 'DDInter', 0);
