INSERT OR IGNORE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5234, 0, 'Ethionamide', 'The concomitant use of protionamide with alcohol may enhance the central nervous system exciting effect of protionamide.  The mechanism is not known.  Excessive use of alcohol with another thiocarbamide derivative ethionamide, has been reported to precipitate a psychotic reaction.  In addition, alcohol tolerance is reported to be reduced during protionamide treatment.', 'alcohol', 'Major', 'Consumption of alcohol during protionamide treatment should be avoided.  The manufacturer of ethionamide also advises that excess alcohol consumption should be avoided during ethionamide therapy (US).', NULL, NULL, 'DDInter', 0);
INSERT OR IGNORE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5235, 0, 'Ethchlorvynol', 'Alcohol may potentiate some of the pharmacologic effects of CNS-active agents.  Use in combination may result in additive central nervous system depression and/or impairment of judgment, thinking, and psychomotor skills.', 'alcohol', 'Moderate', 'Patients receiving CNS-active agents should be warned of this interaction and advised to avoid or limit consumption of alcohol.  Ambulatory patients should be counseled to avoid hazardous activities requiring complete mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', NULL, NULL, 'DDInter', 0);
