INSERT OR IGNORE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5283, 18905, 'Felodipine', 'The consumption of grapefruit juice may be associated with significantly increased plasma concentrations of some calcium channel blockers (CCBs) when they are administered orally.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall by certain compounds present in grapefruit.', 'grapefruit juice', 'Moderate', 'The manufacturers of nifedipine and nisoldipine recommend avoiding grapefruit juice.  Patients treated orally with other calcium channel blockers should be advised to avoid consumption of large amounts of grapefruits and grapefruit juice to prevent any undue fluctuations in serum drug levels.  Increased effects on blood pressure may persist for up to 4 days after the consumption of grapefruit juice.  Monitoring for calcium channel blocker adverse effects (e.g., headache, hypotension, syncope, tachycardia, edema) is recommended.', NULL, NULL, 'DDInter', 0);
