INSERT OR IGNORE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5298, 4802, 'Fentanyl', 'Alcohol may potentiate the central nervous system (CNS) depressant effects of opioid analgesics including fentanyl.  Concomitant use may result in additive CNS depression and impairment of judgment, thinking, and psychomotor skills.  In more severe cases, hypotension, respiratory depression, profound sedation, coma, or even death may occur.', 'alcohol', 'Major', 'Patients should not consume alcoholic beverages or use drug products that contain alcohol during treatment with fentanyl.  Any history of alcohol or illicit drug use should be considered when prescribing fentanyl, and therapy initiated at a lower dosage if necessary.  Patients should be closely monitored for signs and symptoms of sedation, respiratory depression, and hypotension.  Due to a high degree of interpatient variability with respect to grapefruit juice interactions, patients treated with fentanyl should preferably avoid the consumption of grapefruit and grapefruit juice.  In addition, patients receiving transdermal formulations of fentanyl should be cautioned that drug interactions and drug effects may be observed for a prolonged period beyond removal of the patch, as significant amounts of fentanyl are absorbed from the skin for 17 hours or more after the patch is removed.', NULL, NULL, 'DDInter', 0);
