INSERT OR IGNORE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5323, 291, 'Fexofenadine', 'Coadministration with large amounts of certain fruit juices, including grapefruit, orange and apple, may decrease the oral bioavailability of fexofenadine.  The proposed mechanism is inhibition of drug efflux via intestinal organic anion transporting polypeptides (e.g., P-glycoprotein), of which fexofenadine is a substrate.', 'grapefruit juice', 'Moderate', 'To maximize plasma levels and therapeutic effects, fexofenadine should be taken with water.  In addition, patients should refrain from consuming large amounts of grapefruit, orange, or apple juice.', NULL, NULL, 'DDInter', 0);
