INSERT OR IGNORE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5403, 6486, 'Flibanserin', 'Grapefruit juice may increase the plasma concentrations of flibanserin.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall by certain compounds present in grapefruit.', 'grapefruit juice', 'Major', 'Concomitant use of flibanserin with moderate or potent CYP450 3A4 inhibitors such as grapefruit juice is considered contraindicated.  The patient should be advised to avoid the consumption of grapefruit and grapefruit juice during treatment, and to take flibanserin at bedtime to minimize the risk of hypotension, syncope, accidental injury, and central nervous system depression.  In addition, patients should consume no more than 1 to 2 alcoholic drinks and discontinue drinking alcohol at least two hours before taking flibanserin at bedtime; otherwise, they should skip the flibanserin dose that evening.  Alcohol should not be consumed until at least the morning after taking flibanserin at bedtime.  A standard alcoholic drink contains 14 g of pure alcohol and is equivalent to one 12-ounce regular beer (5% alcohol), 5-ounces wine (12% alcohol), or 1.5 ounces of distilled spirits/shot (40% alcohol).', NULL, NULL, 'DDInter', 0);
