INSERT OR IGNORE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5484, 2177, 'Fosphenytoin', 'Acute consumption of alcohol may increase plasma phenytoin levels.  Chronic consumption of alcohol may decrease plasma phenytoin levels.  The mechanism of this interaction is related to induction of phenytoin metabolism by ethanol during chronic administration.  Other hydantoin derivatives may be similarly affected by ethanol.', 'alcohol', 'Moderate', 'Patients receiving phenytoin therapy should be warned about the interaction between phenytoin and ethanol and they should be advised to notify their physician if they experience worsening of seizure control or symptoms of toxicity, including drowsiness, visual disturbances, change in mental status, nausea, or ataxia.', NULL, NULL, 'DDInter', 0);
