INSERT OR IGNORE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5501, 24882, 'Fosphenytoin', 'Acute consumption of alcohol may increase plasma phenytoin levels.  Chronic consumption of alcohol may decrease plasma phenytoin levels.  The mechanism of this interaction is related to induction of phenytoin metabolism by ethanol during chronic administration.  Other hydantoin derivatives may be similarly affected by ethanol.', 'alcohol', 'Moderate', 'Patients receiving phenytoin therapy should be warned about the interaction between phenytoin and ethanol and they should be advised to notify their physician if they experience worsening of seizure control or symptoms of toxicity, including drowsiness, visual disturbances, change in mental status, nausea, or ataxia.', NULL, NULL, 'DDInter', 0);
INSERT OR IGNORE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5502, 33404, 'Fosphenytoin', 'Acute consumption of alcohol may increase plasma phenytoin levels.  Chronic consumption of alcohol may decrease plasma phenytoin levels.  The mechanism of this interaction is related to induction of phenytoin metabolism by ethanol during chronic administration.  Other hydantoin derivatives may be similarly affected by ethanol.', 'alcohol', 'Moderate', 'Patients receiving phenytoin therapy should be warned about the interaction between phenytoin and ethanol and they should be advised to notify their physician if they experience worsening of seizure control or symptoms of toxicity, including drowsiness, visual disturbances, change in mental status, nausea, or ataxia.', NULL, NULL, 'DDInter', 0);
INSERT OR IGNORE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5503, 8300, 'Fosinopril', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation.  Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'alcohol', 'Moderate', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.  Some authorities recommend avoiding alcohol in patients receiving vasodilating antihypertensive drugs.  Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', NULL, NULL, 'DDInter', 0);
INSERT OR IGNORE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5504, 22931, 'Fosinopril', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation.  Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'alcohol', 'Moderate', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.  Some authorities recommend avoiding alcohol in patients receiving vasodilating antihypertensive drugs.  Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', NULL, NULL, 'DDInter', 0);
INSERT OR IGNORE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5505, 8300, 'Fosinopril', 'Moderate-to-high dietary intake of potassium can cause hyperkalemia in some patients who are using angiotensin converting enzyme (ACE) inhibitors.  In some cases, affected patients were using a potassium-rich salt substitute.  ACE inhibitors can promote hyperkalemia through inhibition of the renin-aldosterone-angiotensin (RAA) system.', 'Food High in Potassium', 'Moderate', 'It is recommended that patients who are taking ACE inhibitors be advised to avoid moderately high or high potassium dietary intake.  Particular attention should be paid to the potassium content of salt substitutes.', NULL, NULL, 'DDInter', 0);
