INSERT OR IGNORE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5688, 0, 'Amoxapine', 'Concomitant use of ethanol and a tricyclic antidepressant (TCA) may result altered TCA plasma levels and efficacy, and additive impairment of motor skills, especially driving skills.  Acute ethanol ingestion may inhibit TCA metabolism, while chronic ingestion of large amounts of ethanol may induce hepatic TCA metabolism.', 'alcohol', 'Moderate', 'Patients should be advised to avoid alcohol during TCA therapy.  Alcoholics who have undergone detoxification should be monitored for decreased TCA efficacy.  Dosage adjustments may be required.', NULL, NULL, 'DDInter', 0);
INSERT OR IGNORE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5689, 0, 'Glasdegib', 'Coadministration with grapefruit or grapefruit juice may increase the plasma concentrations of glasdegib.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall induced by certain compounds present in grapefruit.', 'grapefruit juice', 'Moderate', 'Glasdegib may be administered with or without food.  Coadministration of grapefruit or grapefruit juice with glasdegib should preferably be avoided.', NULL, NULL, 'DDInter', 0);
INSERT OR IGNORE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5690, 30066, 'Abiraterone', 'Food may significantly increase the oral bioavailability of abiraterone acetate.  Compared to administration in the fasted state, abiraterone peak plasma concentration (Cmax) and systemic exposure (AUC) were approximately 7- and 5-fold higher, respectively, when a single dose of abiraterone acetate was administered with a low-fat meal (7% fat; 300 calories) and approximately 17- and 10-fold higher, respectively, when it was administered with a high-fat meal (57% fat; 825 calories).  Given the normal variation in the content and composition of meals, taking abiraterone acetate with meals has the potential to result in increased and highly variable exposures.', 'high-fat foods', 'Moderate', 'Abiraterone acetate must be taken on an empty stomach.  No food should be consumed for at least two hours before and one hour after the abiraterone acetate dose.', NULL, NULL, 'DDInter', 0);
INSERT OR IGNORE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5691, 30067, 'Abiraterone', 'Food may significantly increase the oral bioavailability of abiraterone acetate.  Compared to administration in the fasted state, abiraterone peak plasma concentration (Cmax) and systemic exposure (AUC) were approximately 7- and 5-fold higher, respectively, when a single dose of abiraterone acetate was administered with a low-fat meal (7% fat; 300 calories) and approximately 17- and 10-fold higher, respectively, when it was administered with a high-fat meal (57% fat; 825 calories).  Given the normal variation in the content and composition of meals, taking abiraterone acetate with meals has the potential to result in increased and highly variable exposures.', 'high-fat foods', 'Moderate', 'Abiraterone acetate must be taken on an empty stomach.  No food should be consumed for at least two hours before and one hour after the abiraterone acetate dose.', NULL, NULL, 'DDInter', 0);
INSERT OR IGNORE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5692, 489, 'Glimepiride', 'Alcohol may cause hypoglycemia or hyperglycemia in patients with diabetes.  Hypoglycemia most frequently occurs during acute consumption of alcohol.  Even modest amounts can lower blood sugar significantly, especially when the alcohol is ingested on an empty stomach or following exercise.  The mechanism involves inhibition of both gluconeogenesis as well as the counter-regulatory response to hypoglycemia.', 'alcohol', 'Moderate', 'Patients with diabetes should avoid consuming alcohol if their blood glucose is not well controlled, or if they have hypertriglyceridemia, neuropathy, or pancreatitis.  Patients with well controlled diabetes should limit their alcohol intake to one drink daily for women and two drinks daily for men (1 drink = 5 oz wine, 12 oz beer, or 1.5 oz distilled spirits) in conjunction with their normal meal plan.  Alcohol should not be consumed on an empty stomach or following exercise.', NULL, NULL, 'DDInter', 0);
