INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Calcium folinate 50mg vial powder for inj.', 15673, 'كالسيوم فولينات 50مجم فيال بودرة للحقن', '38', NULL, 'Folic acid supplement', 'Folic acid supplement', NULL, 'Leucovorin calcium=folinic acid=calcium folinate', 'Shanxi pude pharm. > acdima international trading', 'Inj', 'حقن', '50mg', '1', NULL, NULL, 'indication for the treatment of osteosarcoma (after high dose methotrexate therapy). used to diminish the toxicity and counteract the effects of impaired methotrexate elimination and of inadvertent overdosages of folic acid antagonists and to treat megalo', NULL, 889, '2022-07-11', NULL, 0, 'For the treatment of osteosarcoma (after high dose methotrexate therapy). Used to diminish the toxicity and counteract the effects of impaired methotrexate elimination and of inadvertent overdosages of folic acid antagonists, and to treat megaloblastic anemias due to folic acid deficiency. Also used in combination with 5-fluorouracil to prolong survival in the palliative treatment of patients with advanced colorectal cancer.', 'As leucovorin is a derivative of folic acid, it can be used to increase levels of folic acid under conditions favoring folic acid inhibition (following treatment of folic acid antagonists such as methotrexate). Leucovorin enhances the activity of fluorouracil by stabilizing the bond of the active metabolite (5-FdUMP) to the enzyme thymidylate synthetase.', 'Leucovorin is one of several active, chemically reduced derivatives of folic acid. It is useful as an antidote to drugs which act as folic acid antagonists. Leucovorin is a mixture of the diastereoisomers of the 5-formyl derivative of tetrahydrofolic acid (THF). The biologically active compound of the mixture is the (-)-l-isomer, known as Citrovorum factor or (-)-folinic acid. Leucovorin does not require reduction by the enzyme dihydrofolate reductase in order to participate in reactions utilizing folates as a source of &ldquo;one-carbon&rdquo; moieties. Administration of leucovorin can counteract the therapeutic and toxic effects of folic acid antagonists such as methotrexate, which act by inhibiting dihydrofolate reductase. Leucovorin has also been used to enhance the activity of fluorouracil.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Magnisol 0.5gm/5ml 6 amps', 7691, 'ماجنيسول 0.5جم/5مل 6 امبولات', '66', '45', 'Magnesium supplements', 'Magnesium supplements', NULL, 'Magnesium sulphate', 'Memphis', 'Amp', 'أمبول', '0.5gm', '6', NULL, NULL, NULL, '6221050010830', 1820, '2024-07-29', NULL, 0, 'Used for immediate control of life-threatening convulsions in the treatment of severe toxemias (pre-eclampsia and eclampsia) of pregnancy and in the treatment of acute nephritis in children. Also indicated for replacement therapy in magnesium deficiency, especially in acute hypomagnesemia accompanied by signs of tetany similar to those of hypocalcemia. Also used in uterine tetany as a myometriat relaxant.', 'Magnesium is the second most plentiful cation of the intracellular fluids. It is essential for the activity of many enzyme systems and plays an important role with regard to neurochemical transmission and muscular excitability. Magnesium sulfate reduces striated muscle contractions and blocks peripheral neuromuscular transmission by reducing acetylcholine release at the myoneural junction. Additionally, Magnesium inhibits Ca<sup>2+</sup> influx through dihydropyridine-sensitive, voltage-dependent channels. This accounts for much of its relaxant action on vascular smooth muscle.', 'Magnesium sulfate is a small colorless crystal used as an anticonvulsant, a cathartic, and an electrolyte replenisher in the treatment of pre-eclampsia and eclampsia. It causes direct inhibition of action potentials in myometrial muscle cells. Excitation and contraction are uncoupled, which decreases the frequency and force of contractions. Magnesium sulfate is gaining popularity as an initial treatment in the management of various dysrhythmias, particularly torsades de pointes, and dyrhythmias secondary to TCA overdose or digitalis toxicity.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Unitense 20 mg 10 f.c. tabs.', 28766, 'يونيتينس 20مجم 10 اقراص', '17', NULL, 'Antihypertensive.ace', 'Antihypertensive.ace', NULL, 'Benazepril', 'International drug agency (idi)', 'Tab', 'أقراص', '20 mg', '1', NULL, NULL, 'about benazepril angiotensin - converting enzyme inhibitor (ace - inhibitor) anti hypertensive. mechanism of action of benazepril it is a prodrug and converted in to active benazeprilat; which is an angiotensin converting enzyme inhibitor prevents the con', NULL, 725, '2022-07-25', NULL, 0, 'Benazepril is indicated for the treatment of hypertension[A840]. It may be used alone or in combination with thiazide diuretics[FDA Label].', 'Benazeprilat, the active metabolite of Benazepril, competes with angiotensin I for binding at the angiotensin-converting enzyme, blocking the conversion of angiotensin I to angiotensin II[FDA Label]. Inhibition of ACE results in decreased plasma angiotensin II[FDA Label]. As angiotensin II is a vasoconstrictor and a negative-feedback mediator for renin activity, lower concentrations result in a decrease in blood pressure and stimulation of baroreceptor reflex mechanisms, which leads to decreased vasopressor activity and to decreased aldosterone secretion[FDA Label].', 'Benazepril, an angiotensin-converting enzyme (ACE) inhibitor, is a prodrug which, when hydrolyzed by esterases to its active Benazeprilat[A836], is used to treat hypertension and heart failure, to reduce proteinuria and renal disease in patients with nephropathies, and to prevent stroke, myocardial infarction, and cardiac death in high-risk patients[A840,A838,A837]. Benazepril and Benazeprilat inhibit angiotensin-converting enzyme (ACE) in human subjects and animals[A840,A836]. ACE is a peptidyl dipeptidase that catalyzes the conversion of angiotensin I to the vasoconstrictor substance, angiotensin II[FDA Label]. Angiotensin II also stimulates aldosterone secretion by the adrenal cortex[FDA Label].', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Jakavi 5 mg 56 tabs.', 21126, 'جاكافي 5مجم 56 قرص', '25,232', '18860', 'Antineoplastic', 'Antineoplastic', NULL, 'Ruxolitinib', 'Novartis', 'Tab', 'أقراص', '5 mg', '4', 'لعلاج الأورام -- علاج السرطان', NULL, 'ruxolitinib is a drug for the treatment of intermediate or high-risk myelofibrosis a type of myeloproliferative disorder that affects the bone marrow and for polycythemia vera (pcv) when there has been an inadequate response to or intolerance of hydroxyur', NULL, 2294, '2024-11-07', NULL, 0, 'Ruxolitinib is indicated for the treatment of the following conditions:



- intermediate or high-risk myelofibrosis (MF), including prima1y MF, post-polycythemia vera MF and post-essential thrombocythemia MF in adults.[L31938] It is also used to treat disease-related splenomegaly or symptoms in adult patients with these conditions.[L32073] 

- polycythemia vera (PV) in adults who have had an inadequate response to or are intolerant of hydroxyurea.[L31938]

- steroid-refracto1y acute graft-versus-host disease (GVHD) in adult and pediatric patients 12 years and older.[L31938]

- chronic GVHD in patients aged 12 years and older who have failed one or two lines of systemic therapy.[L31938]



Topical ruxolitinib is indicated for:



- the short-term and non-continuous chronic treatment of mild to moderate atopic dermatitis in non-immunocompromised patients patients 12 years of age and older whose disease is not adequately controlled with topical prescription therapies or when those therapies are not advisable.[L39125]

- the treatment of non-segmental vitiligo in adult and pediatric patients 12 years of age and older.[L39125, L46068]', 'The Janus kinase (JAK) family of protein tyrosine kinases comprises JAK1, JAK2, JAK3, and non-receptor tyrosine kinase 2 (TYK2).[A229703] JAKs play a pivotal role in intracellular signalling pathways of various cytokines and growth factors essential to hematopoiesis, such as interleukin, erythropoietin, and thrombopoietin.[A7450] JAKs have diverse functions: JAK1 and JAK3 promote lymphocyte differentiation, survival, and function, while JAK2 promotes signal transduction of erythropoietin and thrombopoietin.[A229703, A229753] JAKs are in close proximity to the cytokine and growth factor receptor’s cytoplasmic region. Upon binding of cytokines and growth factors, JAKs are activated, undergoing cross-phosphorylation and tyrosine phosphorylation. This process also reveals selective binding sites for STATs, which are DNA-binding proteins that also bind to the cytoplasmic region of cytokine or growth factor receptors. Activated JAKs and STATs translocate to the nucleus as transcription factors to regulate gene expression of pro-inflammatory cytokines such as IL-6, IL-10, and nuclear factor κB (NF-κB).[A229938] They also activate downstream pathways that promote erythroid, myeloid, and megakaryocytic development.[A229703] 



The molecular pathogenesis of myeloproliferative neoplasms is not fully understood; however, JAK2 is constitutively activated and the JAK-STAT signalling pathway becomes deregulated and aberrant.[A7450, A229938] Ruxolitinib is a selective and potent inhibitor of JAK2 and JAK1, with some affinity against JAK3 and TYK2. Anticancer effects of ruxolitinib are attributed to its inhibition of JAKs and JAK-mediated phosphorylation of STAT3.[L31938] By downregulating the JAK-STAT pathway, ruxolitinib inhibits myeloproliferation and suppresses the plasma levels of pro-inflammatory cytokines such as IL-6 and TNF-α.[A229703] 



Activated JAKs are also implicated in graft-versus-host-disease (GVHD), which is a severe immune complication of allogeneic hematopoietic cell transplantation GVHD is associated with significant morbidity and mortality, especially for patients who do not respond well to corticosteroid therapy. Activated JAKS stimulate T-effector cell responses, leading to increased proliferation of effector T cells and heightened production of pro-inflammatory cytokines. By blocking JAK1 and JAk2, ruxolitinib inhibits donor T-cell expansion and suppresses pro-inflammatory responses.[A229913]', 'Ruxolitinib is an antineoplastic agent that inhibits cell proliferation, induces apoptosis of malignant cells, and reduces pro-inflammatory cytokine plasma levels by inhibiting JAK-induced phosphorylation of signal transducer and activator of transcription (STAT).[A229703] Inhibition of STAT3 phosphorylation, which is used as a marker of JAK activity, [A7450] by ruxolitinib is achieved at two hours after dosing which returned to near baseline by 10 hours in patients with myelofibrosis and polycythemia vera.[L31938] In clinical trials, ruxolitinib reduced splenomegaly and improved symptoms of myelofibrosis.[A229703] In a mouse model of myeloproliferative neoplasms, administration of ruxolitinib was associated with prolonged survival.[A7450] Ruxolitinib inhibits both mutant and wild-type JAK2 [A229703]; however, JAK2V617F mutation, which is often seen in approximately 50% of patients with myelofibrosis, was shown to reduce ruxolitinib sensitivity, which may also be associated with possible resistance to JAK inhibitor treatment.[A229933]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Targocid 200 mg vial for i.v/i.m inj.', 11186, 'تارجوسيد 200مجم فيال', '408', '285', 'Antibiotic with glycopeptide', 'Antibiotic with glycopeptide', NULL, 'Teicoplanin', 'Gruppo lepetit > sanofi', 'Inj', 'حقن', '200 mg', '1', NULL, NULL, 'about teicoplanin glycopeptide antibiotic antibiotic similar to vancomycin. mechanism of action of teicoplanin teicoplanin binds to the terminal dipeptide (amino d alanyl d alanine residue) sequence of peptidoglycan and inhibits the association and cross', '3582910016021', 1932, '2024-12-15', NULL, 0, 'For the treatment of bacterial infections caused by susceptible microorganisms.', 'Teicoplanin inhibits peptidoglycan polymerization, resulting in inhibition of bacterial cell wall synthesis and cell death.', 'It is a glycopeptide antiobiotic extracted from <i>Actinoplanes teichomyceticus</i>, with a similar spectrum of activity to vancomycin. Its mechanism of action is to inhibit bacterial cell wall synthesis. Oral teicoplanin has been demonstrated to be effective in the treatment of pseudomembranous colitis and Clostridium difficile-associated diarrhoea, with comparable efficacy to vancomycin.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Cytarabine stada 50mg/ml (1g) vial', 17029, 'سيتارابين ستادا 50مجم / مل فيال', '134', NULL, 'Antiviral.nucleoside', 'Antiviral.nucleoside', NULL, 'Cytarabine', 'Stada > germa pharm', 'Vial', 'فيال', '50mg', '1', NULL, NULL, 'description a pyrimidine nucleoside analog that is used mainly in the treatment of leukemia especially acute non-lymphoblastic leukemia. indication for the treatment of acute non-lymphocytic leukemia acute lymphocytic leukemia and blast phase of chronic m', NULL, 979, '2022-07-24', NULL, 0, 'For the treatment of acute non-lymphocytic leukemia, acute lymphocytic leukemia and blast phase of chronic myelocytic leukemia.



Cytarabine is indicated in combination with [daunorubicin] for the treatment of newly-diagnosed therapy-related acute myeloid leukemia (t-AML) or AML with myelodysplasia-related changes (AML-MRC) in adults and pediatric patients 1 year and older.[L32843]', 'Cytarabine acts through direct DNA damage and incorporation into DNA. Cytarabine is cytotoxic to a wide variety of proliferating mammalian cells in culture. It exhibits cell phase specificity, primarily killing cells undergoing DNA synthesis (S-phase) and under certain conditions blocking the progression of cells from the G1 phase to the S-phase. Although the mechanism of action is not completely understood, it appears that cytarabine acts through the inhibition of DNA polymerase. A limited, but significant, incorporation of cytarabine into both DNA and RNA has also been reported.', 'Cytarabine is an antineoplastic anti-metabolite used in the treatment of several forms of leukemia including acute myelogenous leukemia and meningeal leukemia. Anti-metabolites masquerade as purine or pyrimidine - which become the building blocks of DNA. They prevent these substances becoming incorporated in to DNA during the "S" phase (of the cell cycle), stopping normal development and division. Cytarabine is metabolized intracellularly into its active triphosphate form (cytosine arabinoside triphosphate). This metabolite then damages DNA by multiple mechanisms, including the inhibition of alpha-DNA polymerase, inhibition of DNA repair through an effect on beta-DNA polymerase, and incorporation into DNA. The latter mechanism is probably the most important. Cytotoxicity is highly specific for the S phase of the cell cycle.', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Ambroxol 7.5mg/ml oral drops 15 ml', 510, 'امبروكسول نقط بالفم 15 مل', '16', '10.5', 'Mucolytic', 'Mucolytic', NULL, 'Ambroxol', 'Glaxo smithkline', 'Drops', 'نقط', '7.5mg', '1', 'مذيب بلغم', NULL, '(indications): this medication is a mucolytic agent prescribed for various respiratory diseases such as emphysema with bronchitis pneumoconiosis chronic inflammatory pulmonary conditions tracheobronchitis (respiratory tract inflammation) bronchiectasis br', '6221045001621', 5800, '2024-11-11', NULL, 0, 'Ambroxol is indicated for secretolytic therapy in bronchoplmonary disease with abnormal mucus secretion and transport. It allows the mucus to be more easily cleared and ease a patient''s breathing.', 'Ambroxol is a mucolytic agent. Excessive Nitric oxide (NO) is associated with inflammatory and some other disturbances of airways function. NO enhances the activation of soluble guanylate cyclase and cGMP accumulation. Ambroxol has been shown to inhibit the NO-dependent activation of soluble guanylate cyclase. It is also possible that the inhibition of NO-dependent activation of soluble guanylate cyclase can suppress the excessive mucus secretion, therefore it lowers the phlegm viscosity and improves the mucociliary transport of bronchial secretions.', NULL, 'DrugBank');
