INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Labipress 5 mg/ml i.v. inf./bolus vial', 7023, 'لابيبريس 5مجم/مل فيال وريدي', '42', NULL, 'Anti-hypertensive.beta blocker.unselective', 'Anti-hypertensive.beta blocker.unselective', NULL, 'Labetalol', 'Debeiky > novell pharma', 'Vial', 'فيال', '5 mg', '1', NULL, NULL, 'about labetalol a mixed alpha/beta adrenergic antagonist antihypertensive(pregnancy-induced hypertension). mechanism of action of labetalol labetalol is an adrenergic antagonist capable of blocking alpha-1 beta-1 and beta-2 receptors and additional weak b', NULL, 1260, '2022-08-07', NULL, 0, 'Labetalol injections are indicated to control blood pressure in severe hypertension.[L7727] Labetalol tablets are indicated alone or in combination with antihypertensives like thiazides and loop diuretics to manage hypertension.[L7730]', 'Labetalol non-selectively antagonizes beta-adrenergic receptors, and selectively antagonizes alpha-1-adrenergic receptors.[A181901] Following oral administration, labetalol has 3 times the beta-blocking ability than alpha-blocking ability.[A181901] This increases to 6.9 times following intravenous administration.[A181901] Antagonism of alpha-1-adrenergic receptors leads to vasodilation and decreased vascular resistance.[A181907] This leads to a decrease in blood pressure that is most pronounced while standing.[A181910] Antagonism of beta-1-adrenergic receptors leads to a slight decrease in heart rate.[L7727] Antagonism of beta-2-adrenergic receptors leads to some of the side effects of labetalol such as bronchospasms, however this may be slightly attenuated by alpha-1-adrenergic antagonism.[A181910] Labetalol leads to sustained vasodilation over the long term without a significant decrease in cardiac output or stroke volume, and a minimal decrease in heart rate.[A181907,A181910]', 'Labetalol antagonizes various adrenergic receptors to decrease blood pressure.[A181901,A181907,A181910,L7727] The duration of action is long as it is generally given twice daily, and the therapeutic window is wide as patients usually take 200-400mg twice daily.[L7730] Patients susceptible to bronchospasms should not use labetalol unless they are unresponsive to or intolerant of other antihypertensives.[L7730]', 'DrugBank');
INSERT OR IGNORE INTO drugs (tradeName, id, arabicName, price, oldPrice, mainCategory, category, category_ar, active, company, dosageForm, dosageForm_ar, concentration, unit, usage, usage_ar, description, barcode, visits, lastPriceUpdate, imageUrl, updatedAt, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology) VALUES ('Oralex oral gel 15 gm', 24209, 'اوراليكس جل للفم 15 جم', '12', NULL, 'Oral care', 'Oral care', NULL, 'Sodium perborate+clove oil+peppermint oil+tea tree oil+sorbitol', 'Kato prestige cosmetics > pharmaserve medical', 'Gel', 'جل', '15 gm', '1', 'جل لعلاج التهابات وقرح الفم واللثة', NULL, NULL, NULL, 978, '2022-07-13', NULL, 0, 'For the treatment of small uncomplicated varicose veins of the lower extremities that show simple dilation with competent valves.', 'Sodium tetradecyl sulfate is a potent toxin for endothelial cells in that brief exposure to even low concentrations are effective in stripping endothelium over a considerable distance and exposing highly thrombogenic endothelium in the process. Diluted sodium tetradecyl sulfate is also able to induce a hypercoagulable state, possibly by selective inhibition of protein C, and can also promote platelet aggregation.', 'Sodium tetradecyl sulfate is an anionic surfactant which occurs as a white, waxy solid. It is used as a sclerosing agent in sclerotherapy. Sclerotherapy is an injection of a sclerosing agent directly through the skin into a lesion and is used primarily for slow-flow vascular anomalies, particularly for venous malformation and lymphatic malformation. Intravenous injection causes intima inflammation and thrombus formation. This usually occludes the injected vein. Subsequent formation of fibrous tissue results in partial or complete vein obliteration that may or may not be permanent.', 'DrugBank');
