    ) VALUES (9611, 'Pantopi 40mg 14 tab', 'بانتوبي 40مجم 14 اقراص', '96', '67', 'Peptic ulcer.proton pump inhibitor', 'Pantoprazole', 'El-obour', 'Tab', 'أقراص', '40mg', '2', 'علاج التهابات القولون والمعدة -- تقليل الالام المعدة والقولون', '

