INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (35, 'Acapril 20mg 20 tab.', 'اكابريل 20مجم 20 قرص', '18', NULL, 'Antihypertensive.ace', 'Enalapril', 'Alfacure pharmaceuticals', 'Tab', 'أقراص', '20mg', '2', 'لعلاج ضغط الدم المرتفع', 'about enalapril angiotensin converting enzyme (ace) inhibitor antihypertensive. mechanism of action of enalapril it is a prodrug and converted in to active enalaprilat; which is an angiotensin converting enzyme inhibitor prevents the conversion of angiote', '6224000875152', NULL, 1341, '2022-11-30', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Enalapril is a prodrug belonging to the angiotensin-converting enzyme (ACE) inhibitor drug class that works on the renin-angiotensin-aldosterone system, which is responsible for the regulation of blood pressure and fluid and electrolyte homeostasis. Enalapril is an orally-active and long-acting nonsulphydryl antihypertensive agent that suppresses the renin-angiotensin-aldosterone system to lower blood pressure. It was developed from a targeted research programmed using molecular modelling.[A18459] Being a prodrug, enalapril is rapidly biotransformed into its active metabolite, [enalaprilat], which is responsible for the pharmacological actions of enalapril. The active metabolite of enalapril competitively inhibits the ACE to hinder the production of angiotensin II, a key component of the renin-angiotensin-aldosterone system that promotes vasoconstriction and renal reabsorption of sodium ions in the kidneys. Ultimately, enalaprilat works to reduce blood pressure and blood fluid volume.

Commonly marketed under the trade name Vasotec, enalapril was first approved by the FDA in 1985 for the management of hypertension, heart failure, and asymptomatic left ventricular dysfunction. It is also found in a combination product containing [hydrochlorothiazide] that is used for the management of hypertension. The active metabolite enalaprilat is also available in oral tablets and intravenous formulations for injection.', '["C09BA02", "C09BB06", "C09BB02", "C09AA02"]', '[]');
