INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (378, 'Allerban s.r. 2 mg 20 tabs.', 'اليربان اس.ار 2مجم 20 قرص', '38', '24', 'Antihistamine.second-generation', 'Ketotifen', 'October pharma > multi-apex', 'Tab', 'أقراص', '2 mg', '2', NULL, 'about ketotifen second generation h1 antagonist mast cell stabilizer antihistamine. mechanism of action of ketotifen it is a mast cell stabilizer. this inhibit the development of airway hyper reactivity (associated with activation of platelets by paf (pla', '6223003200671', NULL, 2350, '2024-10-13', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Ketotifen is a benzocycloheptathiophene derivative[A231204] with potent antihistaminic and mast cell stabilizing properties. It has a similar structure to some other first-generation antihistamines such as [cyproheptadine] and [azatadine].[A231204]

Ketotifen was first developed in Switzerland in 1970 by Sandoz Pharmaceuticals and was initially marketed for the treatment of anaphylaxis.[A231204] In the US, it is now used in an over-the-counter ophthalmic formulation for the treatment of itchy eyes associated with allergies,[L32278] and in Canada a prescription-only oral formulation is available and indicated as an add-on therapy for children with atopic asthma.[L32283] In addition, oral ketotifen is used in Mexico and across Europe for the treatment of various allergic symptoms and disorders,[A231204] including urticaria, mastocytosis, and food allergy.', '["S01GX08", "R06AX17"]', '[]');
