INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (515, 'Amebazol 1gm 2 f.c. tabs.', 'اميبازول 1جم 2 قرص', '27', '18.5', 'Antiprotozoal', 'Secnidazole', 'Egyphar', 'Tab', 'أقراص', '1gm', '1', 'مضاد للطفيليات -- علاج عدوي الجهاز الهضمي -- علاج عدوي المهبل ومجري البول', NULL, '6224001026140', NULL, 4767, '2024-10-31', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Secnidazole is a second-generation 5-nitroimidazole antimicrobial agent. It is structurally related to other 5-nitroimidazoles, including [DB00916] and [DB00911], but displays improved oral absorption and a longer terminal elimination half-life than other drugs in this class. Secnidazole is selective against many anaerobic Gram-positive and Gram-negative bacteria as well as protozoa.[A27210] Once it enters bacteria and parasites, secnidazole is activated by bacterial or parasitic enzymes to form a radical anion, thereby damaging and killing the target pathogen.[A245503]

Secnidazole has been available in many other countries in Europe, Asia, South America, and Africa for decades.[A245503, A245508] In September 2017, FDA approved secnidazole under the market name Solosec for the treatment of trichomoniasis and bacterial vaginosis.[L39524]', '["P01AB07", "J01RA07"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (516, 'Ametic 10mg/2ml 3 amp. for i.m./i.v. inj.', 'اميتيك 10 مجم/2 مل 3 امبولات للحقن عضل/وريد', '4', NULL, 'Antiemetic', 'Metoclopramide', 'Eimc > pharopharma', 'Inj', 'حقن', '10mg', '3', NULL, 'about metoclopramide it is a dopamine antagonist - antiemetic gastroprokinetic agent. mechanism of action of metoclopramide metoclopramide acts through both serotonergic and dopaminergic receptors. metoclopramide binds to serotonin (5 ht3 &5ht4) and dopam', NULL, NULL, 1259, '2023-02-25', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Diabetic gastroparesis is a condition that causes frequent nausea and vomiting, which has a negative impact on quality of life and poses a significant burden on the healthcare system.[A184934] Metoclopramide is a dopamine antagonist used to treat nausea and vomiting that may be associated with diabetic gastroparesis in addition to gastroesophageal reflux disease (GERD). It can also be used to prevent nausea or vomiting associated with chemotherapy or certain surgical or diagnostic procedures.[L8417] 

One unique property of this drug is that it does not increase gastric acid secretion. It is available in the oral tablet form or in solution, and can also be administered through the intravenous route.[T683] Metoclopramide was initially approved by the FDA in 1980.[A184922]', '["A03FA01"]', '[]');
