INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (591, 'Amipride 50mg 20 tab', 'اميبريد 50مجم 20 قرص', '74', '54', 'Psychiatric.antipsychotics', 'Amisulpride', 'Sigma > andalous pharma', 'Tab', 'أقراص', '50mg', '2', 'ما هو دواء أميبرايد', NULL, '6224000396398', NULL, 14169, '2025-08-13', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Amisulpride is a benzamide derivative and a dopamine receptor antagonist that selectively works on dopamine D2 and D3 receptors. As an antipsychotic agent, amisulpride alleviates both positive and negative symptoms of schizophrenia, and it exhibits antidepressant properties in patients with psychiatric disorders, dysthymia, and major depression.[A6755] Amisulpride predominantly works in the limbic system, which explains its relatively lower risk of extrapyramidal adverse effects compared to other atypical antipsychotic agents.[A6752, L32764] Oral tablets of amisulpride is used in European countries as a treatment for acute and chronic schizophrenic disorders, as well as secondary negative symptoms in mental health disorders such as affective disorders, depressive mood, and mental retardation.[L32764]

Amisulpride is also used as an antiemetic agent. In the US, the intravenous formulation of amisulpride is used to treat and prevent postoperative nausea and vomiting in adults, either as monotherapy or in combination with another antiemetic agent of a different drug class.[L32759] It is marketed under the brand name Barhemsys.', '["N05AL05"]', '[]');
