INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (614, 'Amophage 500mg 30 tab', 'اموفاج 500 مجم 30 قرص', '11', NULL, 'Anti-diabetic.sensitizers.biguanide', 'Metformin', 'Amoun', 'Tab', 'أقراص', '500mg', '3', 'علاج مرضى السكر النوع الثاني', NULL, '6221025010179', NULL, 2057, '2022-12-01', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Metformin is a biguanide antihyperglycemic agent and first-line pharmacotherapy used in the management of type II diabetes.[L12207,A176173] 

Metformin is considered an antihyperglycemic drug because it lowers blood glucose concentrations in type II diabetes without causing hypoglycemia. It is commonly described as an "insulin sensitizer", leading to a decrease in insulin resistance and a clinically significant reduction of plasma fasting insulin levels.[A176173] Another well-known benefit of this drug is modest weight loss, making it an effective choice for obese patients type II diabetes.[A36559]

