INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (793, 'Antidiazox 500mg 12 f.c.tab.', 'انتي ديازوكس 500مجم 12 قرص', '43', NULL, 'Antihelminthic', 'Nitazoxanide', 'Global napi pharmaceuticals', 'Tab', 'أقراص', '500mg', '2', NULL, 'about nitazoxanide a synthetic nitrothiazolyl-salicylamide derivative antihelminthics in schistosomiasis. mechanism of action of nitazoxanide it excerts its action by blocking anaerobic energy metabolism. the drug interfering with the pyruvate-ferredoxin', '6223002143122', NULL, 1629, '2023-02-06', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Nitazoxanide belongs to the class of drugs known as _thiazolides_.  Nitazoxanide (NTZ) is a broad-spectrum anti-infective drug that markedly modulates the survival, growth, and proliferation of a range of extracellular and intracellular protozoa, helminths, anaerobic and microaerophilic bacteria, in addition to viruses. This drug is effective in the treatment of gastrointestinal infections including Cryptosporidium parvum or Giardia lamblia in healthy subjects. It is generally well tolerated. Nitazoxanide is a first-line, standard treatment for illness caused by C. parvum or G. lamblia infection in healthy (not immunosuppressed) adults and children and may also be considered in the treatment of illnesses caused by other protozoa or helminths [A31976].  Recently, this drug has been studied as a broad-spectrum antiviral agent due to its ability to inhibit the replication of several RNA and DNA viruses [A31977].', '["P01AX11"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (796, 'Antiflam 50mg 20 tab.', 'انتيفلام 50مجم 20 قرص', '44', '32', 'Nsaid.acetic acid derivatives', 'Diclofenac potassium', 'T3a pharma > vodachem pharmaceuticals', 'Tab', 'أقراص', '50mg', '2', NULL, NULL, '6223005980069', NULL, 3173, '2024-12-10', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Diclofenac is a phenylacetic acid derivative and non-steroidal anti-inflammatory drug (NSAID).[label] NSAIDs inhibit cyclooxygenase (COX)-1 and-2 which are the enzyme responsible for producing prostaglandins (PGs). PGs contribute to inflammation and pain signalling. Diclofenac, like other NSAIDs, is often used as first line therapy for acute and chronic pain and inflammation from a variety of causes. Diclofenac was the product of rational drug design based on the structures of [phenylbutazone], [mefenamic acid], and [indomethacin].[A180796] The addition of two chlorine groups in the ortho position of the phenyl ring locks the ring in maximal torsion which appears to be related to increased potency. It is often used in combination with [misoprostol] to prevent NSAID-induced gastric ulcers. Diclofenac was first approved by the FDA in July 1988 under the trade name Voltaren, marketed by Novartis (previously Ciba-Geigy).[L7360]', '["S01CC01", "S01BC03", "M01AB55", "M02AA15", "M01AB05", "D11AX18"]', '[]');
