INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (2064, 'Cardio-mep 200mg 30 tab.', 'كارديو ميب 200 مجم 30 قرص', '96', '66', 'Antiarrhythmias.class iii', 'Amiodarone', 'Mepaco', 'Tab', 'أقراص', '200mg', '3', 'دواء منظم لمعدل ضربات القلب. ويستخدم لعلاج عدم انتظام دقات القلب البطيني أو الرجفان البطيني', NULL, '6223002212026', NULL, 2502, '2025-04-26', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Amiodarone is a benzofuran derivative, anti-arrhythmic drug used commonly in a variety of settings.[A36817] Most known for its approved indication in life-threatening ventricular arrhythmias, it is also used off-label in the outpatient and inpatient setting for atrial fibrillation. Because of its ability to cause serious toxicity and possibly death, amiodarone use should be reserved for its approved indications, according to prescribing information.[L3561,L11265,L11286]', '["C01BD01"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (2065, 'Sanso d3 10000 i.u. 28 tabs.', 'سانسو د3 10000 وحدة دولية 28 قرص', '180', NULL, 'Vitamin d', 'Cholecalciferol(vitamin d3)', 'AUG PHARMA', 'Tab', 'أقراص', NULL, '2', NULL, NULL, '6223005467317', NULL, 4940, '2022-08-07', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Vitamin D, in general, is a secosteroid generated in the skin when 7-dehydrocholesterol located there interacts with ultraviolet irradiation - like that commonly found in sunlight [L5689]. Both the endogenous form of vitamin D (that results from 7-dehydrocholesterol transformation), vitamin D3 (cholecalciferol), and the plant-derived form, vitamin D2 (ergocalciferol), are considered the main forms of vitamin d and are found in various types of food for daily intake [L5689]. Structurally, ergocalciferol differs from cholecalciferol in that it possesses a double bond between C22 and C23 and has an additional methyl group at C24 [L5689]. Finally, ergocalciferol is pharmacologically less potent than cholecalciferol, which makes vitamin D3 the preferred agent for medical use [L5689].

Appropriate levels of vitamin D must be upheld in the body in order to maintain calcium and phosphorus levels in a healthy physiologic range to sustain a variety of metabolic functions, transcription regulation, and bone metabolism [A223, L5689, L1782, L5771, F4027, F4042, F4048]. However, studies are also ongoing to determine whether or not cholecalciferol may also play certain roles in cancer, autoimmune disorders, cardiovascular disease, and other medical conditions that may be associated with vitamin D deficiency [L5689].', '["M05BB09", "M05BX53", "M05BB07", "M05BB08", "A11CC55", "M05BB05", "A11CC05", "M05BB03", "M05BB04"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (2066, 'Cardiomil 120mg s.r. 10 caps.', 'كارديوميل اس ار 120 مجم 10 كبسولات', '7', NULL, 'Antihypertensive.cardioselective calcium chennel blocker', 'Verapamil', 'Sigma', 'Cap', 'كبسولة', '120mg', '1', NULL, 'about verapamil calcium channel blocker phenylalkylamine derivative class iv antiarrhythmic antihypertensive. mechanism of action of verapamil verapamil is a phenylalkylamine; ca channel blocker. it exerts it`s antianginal antihypertensive antiarrythmic a', '6221051020838', NULL, 1111, '2022-12-03', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Verapamil is a phenylalkylamine calcium channel blocker used in the treatment of high blood pressure, heart arrhythmias, and angina,[L8791] and was the first calcium channel antagonist to be introduced into therapy in the early 1960s.[A188514] It is a member of the non-dihydropyridine class of calcium channel blockers, which includes drugs like [diltiazem] and [flunarizine], but is chemically unrelated to other cardioactive medications.[L8791] Verapamil is administered as a racemic mixture containing equal amounts of the S- and R-enantiomer, each of which is pharmacologically distinct - the S-enantiomer carries approximately 20-fold greater potency than the R-enantiomer, but is metabolized at a higher rate.[A188435]', '["C09BB10", "C08DA51", "C08DA01"]', '[]');
