INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (2447, 'Cetirizine 1% oral dps. 15 ml', 'سيتريزين 15 مل', '9', NULL, 'Antihistamine.antiallergy', 'Cetirizine', 'Pharopharma', 'Unknown', 'غير محدد', '1%', '1', NULL, 'about cetirizine second generation h1 antagonist metabolite of hydroxyzine antihistamine. mechanism of action of cetirizine this drug is a selective peripheral h1 receptor antagonist. thus it inhibits the allergic symptoms produced by histamines like cold', '6221094102195', NULL, 2723, '2022-12-03', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Cetirizine, also commonly known as _Zyrtec_, is an orally active second-generation histamine H1 antagonist proven effective in the treatment of various allergic symptoms, such as sneezing, coughing, nasal congestion, hives, and other symptoms [A175051], [A175054]. 

One of the most common uses for this drug is for a condition called _allergic rhinitis_. The prevalence of allergic rhinitis in the United States is about 15% according to physician diagnoses, and up to 30%, according to self-reported nasal symptoms. Allergic rhinitis is associated with multiple missed or unproductive days at work and school, problems with sleep, and other difficulties with day to day activities for many individuals [A175057]. Furthermore, some antihistamine agents that are used to treat this condition cause undesirable, sedating effects [A175060]. 

