INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (2934, 'Perdofenex 267mg 270 caps', 'بيردوفينيكس 267مجم 270 كبسولة', '19,543', NULL, 'Antifibrotic', 'Pirfenidone', 'Multicare', 'Cap', 'كبسولة', '267mg', '1', 'مضاد لتجلط الرئة', NULL, NULL, NULL, 12890, '2023-01-21', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Pirfenidone is a synthetic pyridone drug.[A251370] It is an antifibrotic agent with anti-inflammatory and antioxidant properties [A251370] that is used to treat idiopathic pulmonary fibrosis (IPF),[L26801] which is a chronic, progressive form of interstitial pneumonia.[A251370] While its mechanism of action is not yet fully understood, pirfenidone is proposed to primarily regulate tumor necrosis factor (TNF) pathways and modulate cellular oxidation.[A251365] The FDA first approved pirfenidone alongside [nintedanib] as one of the first drugs to treat IPF.[A251165]', '["L04AX05"]', '[]');
