INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (17333, 'Dermalex repair atopic eczema for babies & children cream 30 gm', 'ديرمالكس كريم لعلاج الإكزيما التأتبية للأطفال والرضع 30 جم', '100', NULL, 'Eczema', NULL, 'Chefaro > star international company', 'Cream', 'كريم', '30 gm', '1', NULL, '** dermalexï¿½ repair eczema - for babies and children - atopic dermatitis: - skin adjuvant therapy - eczema - for babies & children - from 8 weeks of age - relieves the symptoms - creates a barrier to avoid new flare ups - cortisone free - developed by d', NULL, NULL, 977, '2022-07-25', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (20825, 'Inflacam 20 mg 5 supp.', 'انفلاكام 20مجم 5 لبوسات للكبار', '6', NULL, 'Nsaid.oxicam', 'Piroxicam', 'Mepaco', 'Supp', 'لبوس', '20 mg', '1', NULL, 'about piroxicam nsaid oxicam derivative analgesic antiinflammatory. mechanism of action of piroxicam drug inhibits the biosynthesis and release of prostaglandins by inhibiting cyclooxygenase enzyme in a reversible manner. in this way it reduces the pg (pr', '6223002215003', NULL, 776, '2022-12-06', 'For treatment of osteoarthritis and rheumatoid arthritis.', 'The antiinflammatory effect of Piroxicam may result from the reversible inhibition of cyclooxygenase, causing the peripheral inhibition of prostaglandin synthesis. The prostaglandins are produced by an enzyme called Cox-1. Piroxicam blocks the Cox-1 enzyme, resulting into the disruption of production of prostaglandins. Piroxicam also inhibits the migration of leukocytes into sites of inflammation and prevents the formation of thromboxane A2, an aggregating agent, by the platelets.', 'Piroxicam is in a class of drugs called nonsteroidal anti-inflammatory drugs (NSAIDs). Piroxicam works by reducing hormones that cause inflammation and pain in the body. Piroxicam is used to reduce the pain, inflammation, and stiffness caused by rheumatoid arthritis and osteoarthritis.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (12714, 'Xorin 500mg vial', 'زورين 500مجم فيال', '29', '15', 'Antibiotic.cephalosporin.third-generation', 'Cefotaxime', 'Mup', 'Vial', 'فيال', '500mg', '1', 'مضاد حيوي', 'about cefotaxime third generation cephalosporin parenteral antibiotic. mechanism of action of cefotaxime cefotaxime is a 3rd generation cephalosporin which exerts its bactericidal action against both gram positive & aerobic gram negative organisms by inhi', NULL, NULL, 843, '2025-01-18', 'Used to treat gonorrhoea, meningitis, and severe infections including infections of the kidney (pyelonephritis) and urinary system. Also used before an operation to prevent infection after surgery.', 'The bactericidal activity of cefotaxime results from the inhibition of cell wall synthesis via affinity for penicillin-binding proteins (PBPs). Cefotaxime shows high affinity for penicillin-binding proteins in the cell wall including PBP Ib and PBP III.', 'Cefotaxime is a third generation intravenous cephalosporin antibiotic. It has broad spectrum activity against Gram positive and Gram negative bacteria. It does not have activity against <i>Pseudomonas aeruginosa</i>. Cefotaxime works by inhibiting bacterial cell wall biosynthesis. A positive feature of cefotaxime is that it display a resistance to penicillinases and is useful to treat infections that are resistant to penicillin derivatives.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (33730, 'Supranil 25 mg 30 caps.', 'سوبرانيل 25 مجم 30 كبسولة', '66', '36', 'Psychiatric.antidepressants', 'Clomipramine', 'Alfacure pharmaceuticals', 'Cap', 'كبسولة', '25 mg', '3', NULL, NULL, '6224000875176', NULL, 1046, '2024-11-13', 'May be used to treat obsessive-compulsive disorder and disorders with an obsessive-compulsive component (e.g. depression, schizophrenia, Tourette’s disorder).

Unlabeled indications include: depression, panic disorder, chronic pain (e.g. central pain, idiopathic pain disorder, tension headache, diabetic peripheral neuropathy, neuropathic pain), cataplexy and associated narcolepsy (limited evidence), autistic disorder (limited evidence), trichotillomania (limited evidence), onchophagia (limited evidence), stuttering (limited evidence), premature ejaculation, and premenstrual syndrome. ', 'Clomipramine is a strong, but not completely selective serotonin reuptake inhibitor (SRI), as the active main metabolite desmethyclomipramine acts preferably as an inhibitor of noradrenaline reuptake. &alpha;<sub>1</sub>-receptor blockage and &beta;-down-regulation have been noted and most likely play a role in the short term effects of clomipramine. A blockade of sodium-channels and NDMA-receptors might, as with other tricyclics, account for its effect in chronic pain, in particular the neuropathic type.', 'Clomipramine, a tricyclic antidepressant, is the 3-chloro derivative of Imipramine. It was thought that tricyclic antidepressants work exclusively by inhibiting the re-uptake of the neurotransmitters norepinephrine and serotonin by nerve cells. However, this response occurs immediately, yet mood does not lift for around two weeks. It is now thought that changes occur in receptor sensitivity in the cerebral cortex and hippocampus. The hippocampus is part of the limbic system, a part of the brain involved in emotions. Presynaptic receptors are affected: &alpha;<sub>1</sub> and &beta;<sub>1</sub> receptors are sensitized, &alpha;<sub>2</sub> receptors are desensitized (leading to increased noradrenaline production). Tricyclics are also known as effective analgesics for different types of pain, especially neuropathic or neuralgic pain.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (12911, 'Zofran 4mg/2ml 5 amp. for i.v/i.m inj', 'زوفران 4مجم/2مل 5 أمبولات للحقن عضل/وريد', '138', NULL, 'Antiemetic', 'Ondansetron', 'Glaxo smithkline > sandoz', 'Inj', 'حقن', '4mg', '5', 'مضاد للقيء والترجيع', NULL, '6221045000204', NULL, 2303, '2022-12-15', 'In the adult patient population:

i) orally administered ondansetron tablets and orally disintegrating tablets (ODT) are indicated for:

- the prevention of nausea and vomiting associated with emetogenic cancer chemotherapy, including high dose (ie. greater than or equal to 50 mg/m2) cisplatin therapy, and radiotherapy, and

- the prevention and treatment of postoperative nausea and vomiting



ii) intravenously administered ondansetron injection formulations are indicated for:

- the prevention of nausea and vomiting associated with emetogenic cancer chemotherapy, including high dose (ie. greater than or equal to 50 mg/m2) cisplatin therapy, and

- the prevention and treatment of postoperative nausea and vomiting



In the pediatric (4-18 years of age) patient population:

i) ondansetron was effective and well tolerated when given to children 4-12 years of age for the treatment of post-chemotherapy induced nausea and vomiting,

ii) ondansetron tablets, ondansetron ODT, ondansetron injection are not indicated for the treatment of children 3 years of age or younger,

iii) ondansetron tablets, ondansetron ODT, ondansetron injection are not indicated for use in any age group of the pediatric population for the treatment of post-radiotherapy induced nausea and vomiting, and

iV) ondansetron tablets, ondansetron ODT, ondansetron injection are not indicated for use in any age group of the pediatric population for the treatment of postoperative nausea and vomiting



In the geriatric (>65 years of age) patient population:

i) efficacy and tolerance of ondansetron were similar to that observed in younger adults for the treatment of post-chemotherapy and radiotherapy-induced nausea and vomiting, and

ii) clinical experience in the use of ondansetron in the prevention and treatment of postoperative nausea and vomiting is limited and is not indicated for use in the geriatric patient population ', 'Ondansetron is a selective antagonist of the serotonin receptor subtype, 5-HT3 [F3178, F3181, F3184].



Cytotoxic chemotherapy and radiotherapy are associated with the release of serotonin (5-HT) from enterochromaffin cells of the small intestine, presumably initiating a vomiting reflex through stimulation of 5-HT3 receptors located on vagal afferents [F3178, F3181, F3184]. Ondansetron may block the initiation of this reflex. Activation of vagal afferents may also cause a central release of serotonin from the chemoreceptor trigger zone of the area postrema, located on the floor of the fourth ventricle [F3178, F3181, F3184]. Thus, the antiemetic effect of ondansetron is probably due to the selective antagonism of 5-HT3 receptors on neurons located in either the peripheral or central nervous systems, or both [F3178, F3181, F3184].



Although the mechanisms of action of ondansetron in treating postoperative nausea and vomiting and cytotoxic induced nausea and vomiting may share similar pathways, the role of ondansetron in opiate-induced emesis has not yet been formally established [F3181, F3184].', 'Ondansetron is a highly specific and selective serotonin 5-HT<sub>3</sub> receptor antagonist, not shown to have activity at other known serotonin receptors and with low affinity for dopamine receptors [FDA Label], [A1160, A1161]. The serotonin 5-HT<sub>3</sub> receptors are located on the nerve terminals of the vagus in the periphery, and centrally in the chemoreceptor trigger zone of the area postrema [FDA Label], [A1160, A1161]. The temporal relationship between the emetogenic action of emetogenic drugs and the release of serotonin, as well as the efficacy of antiemetic agents, suggest that chemotherapeutic agents release serotonin from the enterochromaffin cells of the small intestine by causing degenerative changes in the GI tract [FDA Label], [A1160, A1161]. The serotonin then stimulates the vagal and splanchnic nerve receptors that project to the medullary vomiting center, as well as the 5-HT<sub>3</sub> receptors in the area postrema, thus initiating the vomiting reflex, causing nausea and vomiting [FDA Label], [A1160, A1161].



Moreover, the effect of ondansetron on the QTc interval was evaluated in a double-blind, randomized, placebo and positive (moxifloxacin) controlled, crossover study in 58 healthy adult men and women [F3181, F3184]. Ondansetron was tested at single doses of 8 mg and 32 mg infused intravenously over 15 minutes [F3181, F3184]. At the highest tested dose of 32 mg, prolongation of the Fridericia-corrected QTc interval (QT/RR0.33=QTcF) was observed from 15 min to 4 h after the start of the 15 min infusion, with a maximum mean (upper limit of 90% CI) difference in QTcF from placebo after baseline-correction of 19.6 (21.5) msec at 20 min [F3181, F3184]. At the lower tested dose of 8 mg, QTc prolongation was observed from 15 min to 1 h after the start of the 15-minute infusion, with a maximum mean (upper limit of 90% CI) difference in QTcF from placebo after baseline-correction of 5.8 (7.8) msec at 15 min [F3181, F3184]. The magnitude of QTc prolongation with ondansetron is expected to be greater if the infusion rate is faster than 15 minutes [F3181, F3184]. The 32 mg intravenous dose of ondansetron must not be administered [F3181, F3184]. No treatment-related effects on the QRS duration or the PR interval were observed at either the 8 or 32 mg dose [F3181, F3184].



An ECG assessment study has not been performed for orally administered ondansetron [F3181, F3184]. On the basis of pharmacokinetic-pharmacodynamic modelling, an 8 mg oral dose of ondansetron is predicted to cause a mean QTcF increase of 0.7 ms (90% CI -2.1, 3.3) at steady-state, assuming a mean maximal plasma concentration of 24.7 ng/mL (95% CI 21.1, 29.0) [F3181, F3184]. The magnitude of QTc prolongation at the recommended 5 mg/m2 dose in pediatrics has not been studied, but pharmacokinetic-pharmacodynamic modeling predicts a mean increase of 6.6 ms (90% CI 2.8, 10.7) at maximal plasma concentrations [F3181, F3184]. 



In healthy subjects, single intravenous doses of 0.15 mg/kg of ondansetron had no effect on esophageal motility, gastric motility, lower esophageal sphincter pressure, or small intestinal transit time [F3178]. Multiday administration of ondansetron has been shown to slow colonic transit in healthy subjects [F3178]. Ondansetron has no effect on plasma prolactin concentrations [F3178].', 'DrugBank', 0, 0, 0);
