INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (13766, 'Alkyloxan 500mg vial', 'الكيلوكسان 500 مجم فيال', '11', NULL, 'Antineoplastic', 'Cyclophosphamide', 'Choongwae pharma-korea > general technology-egypt', 'Vial', 'فيال', '500mg', '1', NULL, NULL, NULL, NULL, 803, '2022-07-09', 'Cyclophosphamide for intravenous injection is indicated for the treatment of a number of malignancies, including: Hodgkin’s disease, lymphocytic lymphoma, mixed-cell type lymphoma, histiocytic lymphoma, Burkitt’s lymphoma; multiple myeloma, leukemias, mycosis fungoides, neuroblastoma, adenocarcinoma of ovary, retinoblastoma, and breast carcinoma.[L50141]



Cyclophosphamide oral capsules are additionally indicated for the treatment of minimal change nephrotic syndrome in pediatric patients who fail to adequately respond to (or are unable to tolerate) adrenocorticosteroid therapy.[L50557]', 'Alkylating agents work by three different mechanisms: 1) attachment of alkyl groups to DNA bases, resulting in the DNA being fragmented by repair enzymes in their attempts to replace the alkylated bases, preventing DNA synthesis and RNA transcription from the affected DNA, 2) DNA damage via the formation of cross-links (bonds between atoms in the DNA) which prevents DNA from being separated for synthesis or transcription, and 3) the induction of mispairing of the nucleotides leading to mutations.', 'Cyclophosphamide is an antineoplastic in the class of alkylating agents and is used to treat various forms of cancer. Alkylating agents are so named because of their ability to add alkyl groups to many electronegative groups under conditions present in cells. They stop tumor growth by cross-linking guanine bases in DNA double-helix strands - directly attacking DNA. This makes the strands unable to uncoil and separate. As this is necessary in DNA replication, the cells can no longer divide. In addition, these drugs add methyl or other alkyl groups onto molecules where they do not belong which in turn inhibits their correct utilization by base pairing and causes a miscoding of DNA. Alkylating agents are cell cycle-nonspecific. Alkylating agents work by three different mechanisms all of which achieve the same end result - disruption of DNA function and cell death.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (16122, 'Cephlex 250 mg/5 ml susp. 60ml', 'سيفلكس 250 مجم / 5 مل معلق 60 مل', '11', NULL, 'Antibiotic.cephalosporin.first-generation', 'Cefalexin', 'Kahira', 'Susp', 'معلق', '250 mg', '1', NULL, 'about cefalexin a first-generation cephalosporin antibiotic. mechanism of action cephalexin is a 1st generation cephalosporin which exerts its bactericidal action against both gram positive & gram negative organisms by inhibiting bacterial cell wall synth', NULL, NULL, 982, '2022-07-18', NULL, NULL, NULL, NULL, 0, 0, 0);
