INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (28063, 'Terbutanyl 2.5 mg 20 tab.', 'تيربوتانيل 2.5مجم 20 اقراص', '5', NULL, 'Bronchodilator.short-acting b2 agonist', 'Terbutaline', 'Cid', 'Tab', 'أقراص', '2.5 mg', '2', NULL, 'about terbutaline a alfa2-adrenergic agonist a fast-acting bronchodilator tocolytic anti asthma. mechanism of action of terbutaline terbutaline is a ?2 receptor agonist. terbutaline directly relaxes the airway smooth muscles and produces bronchodilation.', NULL, NULL, 773, '2022-07-27', 'Terbutaline is indicated for prevention and reversal of bronchospasm in patients at least 12 years old, with asthma and reversible bronchospasm associated with bronchitis and emphysema.[L32093,L32098]', 'Terbutaline is a selective beta-2 adrenergic receptor agonist.[A230328] Agonism of these receptors in bronchioles activates adenylyl cyclase, increasing intracellular cyclic adenosine monophosphate (cAMP).[A230338] Increased cAMP decreases intracellular calcium, activating protein kinase A, inactivating myosin light-chain kinase, activating myosin light-chain phosphatase, and finally relaxing smooth muscle in the bronchiole.[A230338]', 'Terbutaline is a beta-2 adrenergic receptor agonist indicated to treat reversibly bronchospasm in asthmatic patients with bronchitis and emphysema.[L32093,L32098] It has a short duration as the inhaled form is taken up to three times daily, and the therapeutic window is wide.[L32093,L32098]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (32582, 'Braxone cream 120 gm', 'براكسون كريم 120جم', '140', '120', 'Skin care.moisturizing', 'Honey+propolis+aloe vera ext.+olive oil+chamomile+vitamin e+panthenol+jojoba oil', 'Infinity pharm', 'Cream', 'كريم', '120 gm', '1', NULL, 'braxone fast healing cream speeds up the healing process & provides optimal healing environment. it has debridement effect & anti-inflammatory effect. indications skin dryness ï¿½ nipple fissure ï¿½ cracked skin. ingredients honey ï¿½ propolis ï¿½ aloe ve', NULL, NULL, 535, '2024-07-25', NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (15062, 'Bendatreotid 0.1mg/ml 5 amp. for subcutaneous inj.', 'بنداتريوتيد 0.1 مجم / مل 5 امبول', '690', '530', 'Hypothalmic hormones.somatostatin analogues', 'Octreotide', 'Wasserburger arzneimittel work > pharmacon', 'Inj', 'حقن', '0.1mg', '5', 'بعض أنواع السرطان -- النزيف الناتج عن قرح المعدة والأثنى عشر', NULL, NULL, NULL, 1143, '2025-03-09', 'Octreotide by injection is used for the treatment of acromegaly and the reduction of flushing and diarrhea symptoms related to carcinoid tumors and/or vasoactive intestinal peptide (VIPoma) tumors.[L14513] The delayed-release oral formulation is used for the long-term treatment of acromegaly in patients who tolerate and respond adequately to injectable octreotide and [lanreotide].[L14507,L45528]', 'Octreotide binds to somatostatin receptors coupled to phospholipase C through G proteins and leads to smooth muscle contraction in the blood vessels.[L14519] Downstream effects that stimulate phospholipase C, the production of 1, 4,5-inositol triphosphate, and action on the L-type calcium channels lead to the inhibition of growth hormone, treating the various growth-hormone and metabolic effects of acromegaly.[L14519]



Octreotide''s suppression of luteinizing hormone (LH)[A214724], reduction in splanchnic blood flow[A214727], and inhibition of serotonin, gastrin, vasoactive intestinal peptide, secretin, motilin, and pancreatic polypeptide provide relief for the gastrointestinal and flushing symptoms of carcinoid and/or VIPoma tumors.[A214721]', 'Octreotide mimics the naturally occurring hormone known as somatostatin. Like somatostatin, it demonstrates activity against growth hormone and glucagon, treating the disordered tissue growth and insulin regulation in patients with acromegaly.[L14501,L14513] In addition, octreotide relieves the flushing and diarrhea associated with gastrointestinal tumors by reducing splanchnic blood flow[A214727] and various gastrointestinal hormones associated with diarrhea.[A214721]



Product labeling warns that octreotide may reduce gallbladder contractility, bile secretion, and the release of thyroid-stimulating hormone (TSH) in healthy volunteers.[L14513] In addition, reports of decreased vitamin B12 in patients treated with octreotide have been made. Ensure to monitor vitamin B12 levels in patients taking octreotide.[L14528]', 'DrugBank', 0, 0, 0);
