INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (1261, 'Tocadon f.c.t 30 tablets', 'توكادون 30 قرص', '95', NULL, 'Calcium formula', 'Calcium 200mg+magnesium 75mg+zinc 3.75mg+vitamin d3 200iu vitamin k', 'Novacure', 'Tablet', 'أقراص', NULL, '3', NULL, NULL, NULL, NULL, 913, '2022-09-04', 0, 'Calcium acetate is one of a number of calcium salts used to treat hyperphosphatemia (too much phosphate in the blood) in patients with kidney disease.', 'Calcium acetate and other calcium salts are phosphate binders. They work by binding with the phosphate in the food you eat, so that it is eliminated from the body without being absorbed.', 'Patients with advanced renal insufficiency (creatinine clearance less than 30 ml/min) exhibit phosphate retention and some degree of hyperphosphatemia. The retention of phosphate plays a pivotal role in causing secondary hyperparathyroidism associated with osteodystrophy, and soft-tissue calcification. The mechanism by which phosphate retention leads to hyperparathyroidism is not clearly delineated. Therapeutic efforts directed toward the control of hyperphosphatemia include reduction in the dietary intake of phosphate, inhibition of absorption of phosphate in the intestine with phosphate binders, and removal of phosphate from the body by more efficient methods of dialysis. The rate of removal of phosphate by dietary manipulation or by dialysis is insufficient. Dialysis patients absorb 40% to 80% of dietary phosphorus. Therefore, the fraction of dietary phosphate absorbed from the diet needs to be reduced by using phosphate binders in most renal failure patients on maintenance dialysis. Calcium acetate when taken with meals combines with dietary phosphate to form insoluble calcium phosphate which is excreted in the feces. Maintenance of serum phosphorus below 6.0 mg/dl is generally considered as a clinically acceptable outcome of treatment with phosphate binders. Calcium acetate is highly soluble at neutral pH, making the calcium readily available for binding to phosphate in the proximal small intestine.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (1262, 'Bacticlor 250mg 12 cap', 'بكتيكلور 250مجم 12 كبسولة', '27', NULL, 'Antibiotic.cephalosporin.second-generation', 'Cefaclor', 'Pharco b > ranbaxy', 'Cap', 'كبسولة', '250mg', '2', NULL, NULL, NULL, NULL, 998, '2023-04-19', 0, 'For the treatment of certain infections caused by bacteria such as pneumonia and ear, lung, skin, throat, and urinary tract infections.', 'Cefaclor, like the penicillins, is a beta-lactam antibiotic. By binding to specific penicillin-binding proteins (PBPs) located inside the bacterial cell wall, it inhibits the third and last stage of bacterial cell wall synthesis. Cell lysis is then mediated by bacterial cell wall autolytic enzymes such as autolysins. It is possible that cefaclor interferes with an autolysin inhibitor.', 'Cefaclor is a second generation cephalosporin antibiotic with a spectrum resembling first-generation cephalosporins. <i>In vitro</i> tests demonstrate that the bactericidal action of the cephalosporins results from inhibition of cell-wall synthesis. As indicated by _in vitro_ and _in vivo_ clinical studies, cefaclor was shown to be effective against most strains of Gram positive aerobes - Staphylococci (including coagulase-positive, coagulase-negative, and penicillinase-producing strains), <i>Streptococcus pneumoniae</i>, <i>Streptococcus pyogenes</i> (group A &szlig;-hemolytic streptococci), as well as Gram-negative aerobes - <i>Escherichia coli</i>, <i>Haemophilus influenzae</i> (including &szlig;-lactamase-producing ampicillin-resistant strains), <i>Klebsiella sp</i>, and <i>Proteus mirabilis</i>.', 'DrugBank', 0, 0, 0);
