INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (1439, 'Betaferon 250 mcg/ml pd. for solutn. for s.c. inj.', 'بيتافيرون 250مكجم/مل باودر لعمل محلول للحقن فيال', '450', NULL, 'Antiviral.interferon beta', 'Interferon beta-1b', 'Bayer healthcare', 'Inj', 'حقن', '250 mcg', '1', NULL, NULL, NULL, NULL, 1772, '2022-10-02', 0, 'For the treatment of venereal or genital warts caused by the Human Papiloma Virus.', 'Interferon alpha binds to type I interferon receptors (IFNAR1 and IFNAR2c) which, upon dimerization, activate two Jak (Janus kinase) tyrosine kinases (Jak1 and Tyk2). These transphosphorylate themselves and phosphorylate the receptors. The phosphorylated INFAR receptors then bind to Stat1 and Stat2 (signal transducers and activators of transcription)which dimerize and activate multiple (~100) immunomodulatory and antiviral proteins. Interferon alpha binds less stably to type I interferon receptors than interferon beta.', 'Upregulates the expression of MHC I proteins, allowing for increased presentation of peptides derived from viral antigens. This enhances the activation of CD8+ T cells that are the precursors for cytotoxic T lymphocytes (CTLs) and makes the macrophage a better target for CTL-mediated killing. Interferon alpha also induce the synthesis of several key antiviral mediators, including 2''-5'' oligoadenylate synthetase (2''-5'' A synthetase) and protein kinase R.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (1440, 'Betafos amp. 2ml', 'بيتافوس امبول 2 مل', '31', '21', 'Glucocorticoid', 'Betamethasone dipropionate+betamethasone sodium phosphate', 'Amoun', 'Amp', 'أمبول', '2ml', '1', 'كورتيزون', 'about betamethasone dipropionate a synthetic glucocorticoid anti-inflammatory. indications for betamethasone dipropionate 1.eczema 2.psoriasis 3.contact dermatitis 4.anal and vulval pruritis 5.inflammation of the skin contra-indications of betamethasone d', '6221025019158', NULL, 19818, '2024-09-01', 0, 'As a member of the corticosteroid family, betamethasone is indicated for the treatment of several inflammatory conditions. As topical monotherapy, betamethasone is indicated to relieve pruritic and inflammatory symptoms of corticosteroid-responsive-dermatoses.[L11991,L10782,L11997] Betamethasone can be used topically in combination with a vitamin D analog such as calcipotriene to treat plaque psoriasis.[L12000] The corticosteroid is also available as an injectable suspension and can be used to manage a range of inflammatory conditions including endocrine disorders, gastrointestinal disorders, and rheumatic disorders among other conditions.[L11994]

', 'Glucocorticoids inhibit neutrophil apoptosis and demargination, and inhibit NF-Kappa B and other inflammatory transcription factors.[A187463] They also inhibit phospholipase A2, leading to decreased formation of arachidonic acid derivatives.[A187463] In addition, glucocorticoids promote anti-inflammatory genes like interleukin-10.[A187463]



Corticosteroids like betamethasone can act through nongenomic and genomic pathways.[A31458] The genomic pathway is slower and occurs when glucocorticoids activate glucocorticoid receptors and initiate downstream effects that promote transcription of anti-inflammatory genes including phosphoenolpyruvate carboxykinase (PEPCK), IL-1-receptor antagonist, and tyrosine amino transferase (TAT).[A31458] On the other hand, the nongenomic pathway is able to elicit a quicker response by modulating T-cell, platelet and monocyte activity through the use of existing membrane-bound receptors and second messengers.[A31458]', 'Corticosteroids bind to the glucocorticoid receptor inhibiting pro-inflammatory signals, while promoting anti-inflammatory signals.[A187463] Corticosteroids have a wide therapeutic window as patients may require doses that are multiples of what the body naturally produces.[A187463] Patients who require long-term treatment with a corticosteroid should be counselled regarding the risk of hypothalamic-pituitary-adrenal axis suppression and increased susceptibility to infections.[A187463]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (1441, 'Betafucin cream 15 gm', 'بيتافيوسين كريم 15 جرام', '37', '25.5', 'Antibiotic with glucocorticoid', 'Betamethasone+fucidic acid', 'Delta pharma', 'Cream', 'كريم', '15 gm', '1', NULL, 'description betamethasone is a type of medicine called a topical corticosteroid. corticosteroids are medicines used for reducing inflammation. skin inflammation in eczema occurs due to the irritation of the skin and is caused by the release of various sub', '6221163000599', NULL, 5640, '2024-07-31', 0, NULL, NULL, NULL, NULL, 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (1442, 'Betagan 0.5% eye drops 5 ml', 'بيتاجان قطرة 5عين مل', '22', NULL, 'Antiglaucoma', 'Levobunolol hydrochloride 0.5%', 'Allergan > soficopharm', 'Drops', 'نقط', '0.5%', '1', NULL, 'about levobunolol a non-selective beta blocker antiglaucoma(topical). mechanism of action of levobunolol levobunolol is a non selective beta blocker. it inhibits the beta receptors (?2) present in the eye such as in the ciliary body epithelium and blood v', '5016007203325', NULL, 1031, '2022-12-03', 0, 'For lowering intraocular pressure (IOP) and may be used in patients with chronic open-angle glaucoma or ocular hypertension.', 'Levobunolol''s mechanism of action in reducing IOP is not clearly defined, but is believed to be due to a reduction of the production of aqueous humor via blockage of endogenous catecholamine-stimulated increases in cyclic adenosine monophosphate (AMP) concentrations within the ciliary processes.', 'Levobunolol is an ophthalmic beta-blocker, equally effective at &beta;(1)- and &beta;(2)-receptor sites. Levobunolol reduces both elevated and normal IOP in patients with or without glaucoma. In patients with elevated IOP, levobunolol reduces mean IOP by approximately 25-40% from baseline. As the drug is a nonselective &beta-adrenergic blocking agent, it can produce both systemic pulmonary and cardiovascular effects following topical application to the eye. These effects include adverse pulmonary effects (eg. bronchoconstriction, increased airway resistance), and a decrease in blood pressure and heart rate. ', 'DrugBank', 0, 0, 0);
