    ) VALUES (12945, 'Zurcal 40mg 28 gastro resistant tabs.', 'زوركال 40مجم 28 قرص', '192', '132', 'Peptic ulcer.proton pump inhibitor', 'Pantoprazole', 'Marcyrl co. > aug pharma', 'Tab', 'أقراص', '40mg', '2', 'حرقان المعدة والصدر بسبب الحموضة.', '

