INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (3289, 'Daktarin 2% oral gel 40 gm', 'دكتارين 40 جم جيل للفم', '54', '33.5', 'Antifungals.imidazoles', 'Miconazole', 'Janssen cilag > soficopharm', 'Gel', 'جل', '2%', '1', 'مضاد لفطريات الفم', 'about miconazole an imidazole derivative broad spectrum antifungal. mechanism of action of miconazole miconazole is fungicidal or fungistatic depending on the drug concentrations. i t inhibits the conversion of lanosterol to 14 demethyl lanosterol by inhi', '05413868107265', NULL, 11532, '2023-10-28', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Miconazole is a broad-spectrum azole antifungal with some activity against Gram-positive bacteria as well.[A203636] It is widely used to treat mucosal yeast infections, including both oral and vaginal infections; although intravenous miconazole is no longer available, a wide variety of suppositories, creams, gels, and tablet-based products are available.[L14021, L14024, L14027, L14033, L14396] Miconazole is thought to act primarily through the inhibition of fungal CYP450 14α-lanosterol demethylase activity.[A203636, A203639]

Miconazole was first synthesized in 1969 and first granted FDA approval on January 8, 1974, for sale by INSIGHT Pharmaceuticals as a topical cream.[A214523, L14021] It is currently available as a variety of prescription and over the counter products. Despite having been in clinical use for an extended period, resistance to miconazole among susceptible organisms is relatively low.[A203636]', '["A07AC01", "J02AB01", "D01AC52", "G01AF20", "A01AB09", "D01AC02", "S02AA13", "G01AF04"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (3290, 'Dalacin c 600mg i.m./i.v. 4 ml amp.', 'دالاسين سي 600مجم امبول', '148', '92.5', 'Antibiotic.lincomycins', 'Clindamycin', 'Pfizer', 'Amp', 'أمبول', '600mg', '1', 'مضاد حيوي', 'mechanism clindamycin works by preventing bacteria from producing proteins that are essential to them. without these proteins the bacteria cannot grow replicate and increase in numbers. the remaining bacteria eventually die or are destroyed by the immune', '6221050010328', NULL, 3798, '2025-03-01', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Clindamycin is a semi-synthetic lincosamide antibiotic used in the treatment of a variety of serious infections due to susceptible microorganisms[L11599,L11596] as well as topically for acne vulgaris.[L11593] It has a relatively narrow spectrum of activity that includes anaerobic bacteria as well as gram-positive cocci and bacilli and gram-negative bacilli.[A190621] Interestingly, clindamycin appears to carry some activity against protozoans, and has been used off-label in the treatment of toxoplasmosis, malaria, and babesiosis.[A190657]

Clindamycin is derived from, and has largely replaced, [lincomycin], a naturally occurring lincosamide and the eponymous member of this antibiotic class, due to its improved properties over the parent compound. The name lincomycin is derived from Lincoln, Nebraska, where it was first isolated from _Streptomyces lincolnensis_ found in a soil sample.[A190621]', '["D10AF01", "J01FF01", "D10AF51", "G01AA10"]', '[]');
