INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (4029, 'Egycusate 20 mg/5 ml syrup 100ml', 'ايجيكيوسات شراب 100 مل', '25', '15.5', 'Laxative', 'Docusate sodium', 'Egpi', 'Syrup', 'شراب', '20 mg', '1', NULL, 'docusate is a stool softener. it works by increasing the amount of water the stool absorbs in the gut making the stool softer and easier to pass. side effects stomach pain diarrhea or cramping may occur. irritated throat (with liquid or syrup forms) may a', '6223003930646', NULL, 3470, '2024-07-28', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Docusate, or dioctyl sulfosuccinate, is a stool softener indicated for the treatment of constipation[A32201]. Docusate acts by increasing the amount of water the stool absorbs in the gut, making the stool softer and easier to pass [L1801]. Docusate can be orally or rectally administered. Docusate is on the World Health Organization''s List of Essential Medicines[L5915]. However the effectiveness of docusate in treating constipation remains unclear, as several studies report docusate to be no more effective than placebo for increasing the frequency of stool or stool softening [A32200,A32202,A176987]. Recently there has been pressure to stop prescribing docusate as it has been identified as an ineffective medicine[A176972,A176987,L5912]. Additionally, it does not appear to lessen symptoms associated with constipation such as abdominal cramps. Still docusate is available in over-the-counter products as a common laxative.', '[]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (4032, 'Sunlamotri 50 mg 30 disp./chew. tabs.', 'سانلاموتري 50مجم 30 قرص مضغ', '93', '23', 'Anti-epileptic.triazine', 'Lamotrigine', 'Sun pharmaceutical industries ltd.', 'Tab', 'أقراص', '50 mg', '1', NULL, NULL, NULL, NULL, 915, '2025-06-03', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Lamotrigine is an antiepileptic drug belonging in the phenyltriazine class. It is used in the treatment of both epilepsy and as a mood stabilizer in bipolar disorder. Lamotrigine is the first medication since lithium granted Food and Drug Administration (FDA) approval for the maintenance treatment of bipolar type I. It is approved for use in more than 30 countries.[A191350]

Lamotrigine has relatively few side-effects and does not require laboratory monitoring. While it is indicated for epilepsy and bipolar disorders, there is evidence that lamotrigine could have some clinical efficacy in certain neuropathic pain states.[A849,A850]', '["N03AX09"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (4034, 'Egyferon 5 m.i.u. vial', 'ايجي فيرون 5 مليون وحده دولية فيال', '85', NULL, 'Antiviral .interferon alfa', 'Interferon alfa-2b', 'El nile.', 'Vial', 'فيال', NULL, '1', NULL, 'indication for the treatment of hairy cell leukemia malignant melanoma and aids-related kaposi s sarcoma. pharmacodynamics upregulates the expression of mhc i proteins allowing for increased presentation of peptides derived from viral antigens. this enhan', '6221077200610', NULL, 971, '2022-12-04', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Interferon alpha 2b (human leukocyte clone hif-sn 206 protein moiety reduced). A type I interferon consisting of 165 amino acid residues with arginine in position 23. This protein is produced by recombinant DNA technology and resembles interferon secreted by leukocytes. It is used extensively as an antiviral or antineoplastic agent.', '["L03AB05"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (4037, 'Egygastreaz 100mg/5ml pd. for oral 60ml susp', 'ايجي جاستريز 100 مجم/5مل معلق 60 مل', '31', '19.5', 'Anthelmentic', 'Nitazoxanide', 'Egpi', 'Susp', 'معلق', '100mg', '1', 'يستخدم هذا الدواء لعلاج الإسهال الناجم عن بعض أنواع العدوى الطفيلية, والاسهال الذي يعرف بإسم(إسهال المسافر).', NULL, '6223003930011', NULL, 4074, '2024-07-30', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Nitazoxanide belongs to the class of drugs known as _thiazolides_.  Nitazoxanide (NTZ) is a broad-spectrum anti-infective drug that markedly modulates the survival, growth, and proliferation of a range of extracellular and intracellular protozoa, helminths, anaerobic and microaerophilic bacteria, in addition to viruses. This drug is effective in the treatment of gastrointestinal infections including Cryptosporidium parvum or Giardia lamblia in healthy subjects. It is generally well tolerated. Nitazoxanide is a first-line, standard treatment for illness caused by C. parvum or G. lamblia infection in healthy (not immunosuppressed) adults and children and may also be considered in the treatment of illnesses caused by other protozoa or helminths [A31976].  Recently, this drug has been studied as a broad-spectrum antiviral agent due to its ability to inhibit the replication of several RNA and DNA viruses [A31977].', '["P01AX11"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (4038, 'Egynir 125mg/5ml pd. for oral susp. 60ml', 'ايجينير 125مجم/5مل معلق 60 مل', '49', '32.5', 'Antibiotic.cephalosporin.third-generation', 'Cefdinir', 'Egyphar', 'Susp', 'معلق', '125mg', '1', 'مضاد حيوي يستخدم لعلاج التهاب الجيوب الأنفية--التهاب الأذن الوسطى--التهاب اللوزتين--الالتهاب الرئوي.', 'about cefdinir semisynthetic (third generation ) cephalosporin antibiotic oral broad-spectrum antibiotic. mechanism of action cefdinir is a 3rd generation cephalosporin which exerts its bactericidal action against both gram positive & aerobic gram negativ', '6224001026706', NULL, 1670, '2025-01-04', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Cefdinir, also known as Omnicef, is a semi-synthetic, broad-spectrum antibiotic belonging to the third generation of the cephalosporin class. It has been proven to be effective for the treatment of common bacterial infections in the ear, sinus, throat, lungs, and skin. Cefdinir was approved by the FDA in 1997 to treat a variety of mild to moderate infections and was initially marketed by Abbvie.[A180739] Because of its chemical structure, it is effective against organisms that are resistant to first-line cephalosporin therapy due to the production of beta-lactamase enzymes.[A180724,A180727]', '["J01DD15"]', '[]');
