INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (12375, 'Visipaque 320mg i/ml (20ml) vial', 'فيسيباك 320مجم/مل (20مل) فيال', '40', NULL, 'Radiographic procedures.contrast agent', 'Iodixanol', 'Nycomed > t3a pharma', 'Vial', 'فيال', '320mg', '1', NULL, 'description iodixanol is a nonionic hydrophilic compound commonly used as a contrast agent during coronary angiography particularly in individuals with renal dysfunction as it is believed to be less toxic to the kidneys than most other intravascular contr', NULL, NULL, 822, '2022-08-06', 'Iodixanol is a contrast agent during coronary angiography.', 'Organic iodine compounds attenuate x-rays as they pass through the body, thereby allowing the body structures containing iodine to be delineated in contrast to those structures that do not contain iodine. The degree of opacity produced by these compounds is directly proportional to the total amount (concentration and volume) of the iodinated contrast agent in the path of the x-rays. After intravascular administration, iodixanol makes opaque those internal structures in its path of flow, allowing their visualization until significant hemodilution and elimination occur.', 'Iodixanol is a contrast agent commonly used during coronary angiography, particularly in individuals with renal dysfunction, as it is believed to be less toxic to the kidneys than most other intravascular contrast agents. It is an imaging contrast agent with the same osmolality as blood (290mOsm/kg H20).', 'DrugBank', 0, 0, 0);
