INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (23265, 'Narcotan inhaler 100ml', 'ناركوتان انهيلر 100مل', '40', NULL, 'General anaesthesia', 'Halothane', 'Pharco > eimc', 'Inhaler', 'جهاز استنشاق', '100ml', '1', NULL, 'about halothane haloalkane derivative inhalation general anesthetic. mechanism of action of halothane halothane will alter presynaptic activity and the release of neurotransmitters due to alterations in intracellular signaling. halothane similarly inhibit', NULL, NULL, 740, '2022-07-30', 'For the induction and maintenance of general anesthesia', 'Halothane causes general anaethesia due to its actions on multiple ion channels, which ultimately depresses nerve conduction, breathing, cardiac contractility. Its immobilizing effects have been attributed to its binding to potassium channels in cholinergic neurons. Halothane''s effect are also likely due to binding to NMDA and calcium channels, causing hyperpolarization.', 'Halothane is a general inhalation anesthetic used for induction and maintenance of general anesthesia. It reduces the blood pressure and frequently decreases the pulse rate and depresses respiration. It induces muscle relaxation and reduces pains sensitivity by altering tissue excitability. It does so by decreasing the extent of gap junction mediated cell-cell coupling and altering the activity of the channels that underlie the action potential.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (7015, 'Solovit lozenges honey&lemon 24 lozenges', 'سولوفيت استحلاب ليمون وعسل 24 قطعة', '75', NULL, 'Multivitamin', 'Vitamin c+zinc+black elderberry+honey', 'Solovit for dietary supplement', 'Sol', 'محلول', NULL, '1', NULL, NULL, NULL, NULL, 1018, '2025-08-17', 'For the treatment of vitamin A deficiency.', 'Vision:Vitamin A (all-<i>trans</i> retinol) is converted in the retina to the 11-<i>cis</i>-isomer of retinaldehyde or 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal functions in the retina in the transduction of light into the neural signals necessary for vision. 11-<i>cis</i>-retinal, while attached to opsin in rhodopsin is isomerized to all-<i>trans</i>-retinal by light. This is the event that triggers the nerve impulse to the brain which allows for the perception of light. All-<i>trans</i>-retinal is then released from opsin and reduced to all-<i>trans</i>-retinol. All-<i>trans</i>-retinol is isomerized to 11-<i>cis</i>-retinol in the dark, and then oxidized to 11-<i>cis</i>-retinal. 11-<i>cis</i>-retinal recombines with opsin to re-form rhodopsin. Night blindness or defective vision at low illumination results from a failure to re-synthesize 11-<i>cis</i> retinal rapidly. Epithelial differentiation: The role of Vitamin A in epithelial differentiation, as well as in other physiological processes, involves the binding of Vitamin A to two families of nuclear retinoid receptors (retinoic acid receptors, RARs; and retinoid-X receptors, RXRs). These receptors function as ligand-activated transcription factors that modulate gene transcription. When there is not enough Vitamin A to bind these receptors, natural cell differentiation and growth are interrupted.', 'Vitamin A is effective for the treatment of Vitamin A deficiency. Vitamin A refers to a group of fat-soluble substances that are structurally related to and possess the biological activity of the parent substance of the group called all-<i>trans</i> retinol or retinol. Vitamin A plays vital roles in vision, epithelial differentiation, growth, reproduction, pattern formation during embryogenesis, bone development, hematopoiesis and brain development. It is also important for the maintenance of the proper functioning of the immune system.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (19628, 'Gazyvaro 1000mg/40ml vial i.v. inf.', 'جازيفارو 1000 مجم / 40 مل فيال', '126,300', '85000', 'Antineoplastic', 'Obinutuzumab', 'F.hoffman la roche', 'Vial', 'فيال', '1000mg', '1', NULL, 'gazyvaro is the first type ii glycoengineered antibody to target cells that have the cd20 marker on their surface. these cells are central to many blood cancers including common forms of lymphoma and leukemia.', NULL, NULL, 1611, '2025-11-04', 'Obinutuzumab, in combination with [chlorambucil], is indicated for the treatment of patients with previously untreated chronic lymphocytic leukemia.[L50617]



It is also used to treat follicular lymphoma, in combination with [bendamustine] followed by monotherapy, in patients who relapsed after or are refractory to, a [rituximab]-containing regimen.[L50617]



Obinutuzumab is also used in combination with chemotherapy followed by monotherapy to treat previously untreated stage II bulky, III or IV follicular lymphoma in adult patients achieving at least a partial remission.[L50617]', 'In contrast to rituximab, which is a classic type I CD20 antibody, obinutuzumab binds to type II CD20 antibodies. This allows obinutuzumab to have a much higher induction of antibody-dependant cytotoxicity and a higher direct cytotoxic effect than the classic CD20 antibodies.

', 'Obinutuzumab is more potent than rituximab in depleting B-cells, antitumor activity, and tumor regression.', 'DrugBank', 0, 0, 0);
