INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (21259, 'Karvex 6.25mg 30 tab.', 'كارفيكس 6.25 مجم 30 قرص', '20', NULL, 'Antihypertensive.beta blocker.unselective', 'Carvedilol', 'Sigma', 'Tab', 'أقراص', '6.25mg', '3', NULL, 'about carvedilol beta blocker with alpha- adrenergic blocking activity antihypertensive and anti angina. mechanism of action of carvedilol carvedilol is an adrenergic antagonist capable of blocking alpha-1 beta-1 and beta-2 receptors. it produces vasodila', '6221051012932', NULL, 807, '2022-12-06', 'Carvedilol is indicated to treat mild to severe heart failure, left ventricular dysfunction after myocardial infarction with ventricular ejection fraction ≤40%, or hypertension.[L7889,L7892]', 'Carvedilol inhibits exercise induce tachycardia through its inhibition of beta adrenoceptors.[A182306] Carvedilol''s action on alpha-1 adrenergic receptors relaxes smooth muscle in vasculature, leading to reduced peripheral vascular resistance and an overall reduction in blood pressure.[A1929,A182306] At higher doses, calcium channel blocking and antioxidant activity can also be seen.[A1929] The antioxidant activity of carvedilol prevents oxidation of low density lipoprotein and its uptake into coronary circulation.[A1929]', 'Carvedilol reduces tachycardia through beta adrenergic antagonism and lowers blood pressure through alpha-1 adrenergic antagonism.[L7889,L7892] It has a long duration of action as it is generally taken once daily and has a broad therapeutic index as patients generally take 10-80mg daily.[L7889,L7892] Patients taking carvedilol should not abruptly stop taking this medication as this may exacerbate coronary artery disease.[L7889,L7892]', 'DrugBank', 0, 0, 0);
