INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (16451, 'Claudicat 100mg 30 tab.', 'كلوديكات 100مجم 30 قرص', '70', NULL, 'Antiplatelet.phosphodiesterase 3 inhibitor', 'Cilostazol', 'Jedco int. co. for pharmaceuticals > four a pharma', 'Tab', 'أقراص', '100mg', '3', NULL, NULL, '6225000110601', NULL, 848, '2023-04-18', 'Indicated for the alleviation of symptoms of intermittent claudication (pain in the legs that occurs with walking and disappears with rest).', 'Cilostazol and several of its metabolites are cyclic AMP (cAMP) phosphodiesterase III inhibitors (PDE III inhibitors), inhibiting phosphodiesterase activity and suppressing cAMP degradation with a resultant increase in cAMP in platelets and blood vessels, leading to inhibition of platelet aggregation and vasodilation.', 'Cilostazol reduces the symptoms of intermittent claudication, as indicated by an increased walking distance. Intermittent claudication is pain in the legs that occurs with walking and disappears with rest. The pain occurs due to reduced blood flow to the legs.', 'DrugBank', 0, 0, 0);
