INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (13871, 'Alzaglob 10mg 10 scored f.c. tab.', 'الزجلوب 10 مجم 10 قرص', '45', NULL, 'Central acetylcholinesterase inhibitor', 'Donepezil', 'Medizen pharmaceutical industries > global health care-egypt', 'Tab', 'أقراص', '10mg', '1', 'يستخدم العلاج في بعض حالات الخرف الخفيفة إلى المتوسطة المترافقة مع داء الزهايمر', 'about donepezil central acetylcholinesterase inhibitor antidementia agent in alzheimer`s disease. mechanism of action of donepezil alzheimer`s disease may result from a deficiency in neurotransmitters used by nerves in the brain to communicate with one an', NULL, NULL, 831, '2022-07-13', 'Donepezil, administered orally[L7916] or via transdermal delivery system,[L41125] is indicated for the treatment of dementia of the Alzheimer''s type. It is also available as an extended-release capsule in combination with [memantine] for the treatment of moderate-to-severe dementia of the Alzheimer''s type in patients previously stabilized on 10mg of donepezil hydrochloride once daily.[L7937] 



Off-label uses include the management of vascular dementia, Parkinson''s Disease-associated dementia, and Lewy body dementia, amongst others.[A182333,T668]', '

The commonly accepted cholinergic hypothesis[A182369] proposes that a portion of the cognitive and behavioral decline associated with Alzheimer''s are the result of decreased cholinergic transmission in the central nervous system. Donepezil selectively and reversibly inhibits the acetylcholinesterase enzyme, which normally breaks down acetylcholine. The main pharmacological actions of this drug are believed to occur as the result of this enzyme inhibition, enhancing cholinergic transmission, which relieves the symptoms of Alzheimer''s dementia. In addition to the above, other mechanisms of action of donepezil are possible, including the opposition of glutamate-induced excitatory transmission via downregulation of NMDA receptors and the regulation of amyloid proteins, which have demonstrated significant effects on the disease process of Alzheimer''s.[A182333,A182351,A182444] Other possible targets for donepezil may also include the inhibition various inflammatory signaling pathways, exerting neuroprotective effects.[A182339,A182393]', 'By inhibiting the acetylcholinesterase enzyme, donepezil improves the cognitive and behavioral signs and symptoms of Alzheimer''s Disease, which may include apathy, aggression, confusion, and psychosis.[A182333,A182408]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (9910, 'Ritopar 10mg 20 f.c. tabs.', 'ريتوبار 10مجم 20 قرص', '19', NULL, 'Uterine relaxant', 'Ritodrine', 'Egyphar', 'Tab', 'أقراص', '10mg', '2', NULL, 'about ritodrine a beta-2 adrenergic agonist a tocolytic(uterine relaxant). mechanism of action of ritodrine ritodrine is 2 adrenergic agonist. it binds to ?2 adrenergic receptor on outer membrane of myometrial cell and activates adenylyl cyclase. this wil', '6224001026041', NULL, 585, '2023-01-14', 'For the treatment and prophylaxis of premature labour', 'Ritodrine is beta-2 adrenergic agonist. It binds to beta-2 adrenergic receptors on outer membrane of myometrial cell, activates adenyl cyclase to increase the level of cAMP which decreases intracellular calcium and leads to a decrease of uterine contractions.', 'Beta-2 adrenergic receptors are located at sympathetic neuroeffector junctions of many organs, including uterus. Ritodrine is beta-2 adrenergic agonist. It stimulates beta-2 adrenergic receptor, increases cAMP level and decreases intracellular calcium concentration. The decrease of calcium concentration leads to a relaxation of uterine smooth muscle and, therefore, a decrease in premature uterine contractions. ', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (25498, 'Puregon 50 i.u./0.5ml vial', 'يوريجون 50 وحدة/0.5مل فيال', '140', NULL, 'Infertility drugs.follicle stimulating hormone', 'Follitropin beta', 'Organon > multipharma', 'Vial', 'فيال', '0.5ml', '1', 'معالج ومساعد للإخصاب للنساء اللواتي لم يستطعن ​​الحمل بسبب مشاكل الإباضة.', 'about follitropin beta follicle-stimulating hormone beta subunit in infertility recombinant dna origin mechanism of action of follitropin beta fsh the active component of follitropin beta is the primary hormone responsible for follicular recruitment and d', NULL, NULL, 1915, '2022-07-16', 'In women having been diagnosed with primary ovarian failure, it is used in combination with human chorionic gonadotropin (hCG) to assist in ovulation and fertility. In men with hypogonadotrophic hypogonadism, it is used to induce spermatogenesis. Follitropin may also be used to cause the ovary to produce several follicles, which can then be harvested for use in gamete intrafallopian transfer (GIFT) or in vitro fertilization (IVF).', 'Follitropin is a recombinant form of endogenous follicle stimulating hormone (FSH). FSH binds to the follicle stimulating hormone receptor which is a G-coupled transmembrane receptor. Binding of the FSH to its receptor seems to induce phosphorylation and activation of the PI3K (Phosphatidylinositol-3-kinase) and Akt signaling pathway, which is known to regulate many other metabolic and related survival/maturation functions in cells.', 'Used for the treatment of female infertility, Follitropin beta or follicle stimulating hormone (FSH) stimulates ovarian follicular growth in women who do not have primary ovarian failure. FSH, the active component of Follitropin beta is the primary hormone responsible for follicular recruitment and development.', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (12694, 'Ximacef 100 mg/5ml susp. 60 ml', 'زيماسيف 100مجم/5مل معلق 60 مل', '48', NULL, 'Antibiotic.cephalosporin.third-generation', 'Cefixime', 'Sigma', 'Susp', 'معلق', '100 mg', '1', NULL, NULL, '6221172002416', NULL, 1272, '2022-10-18', 'Cefixime is indicated for the treatment of uncomplicated urinary tract infections caused by _Escherichia coli_ and _Proteus mirabilis_, otitis media caused by _Haemophilus influenzae_, _Moraxella catarrhalis_, and _Streptococcus pyogenes_, pharyngitis and tonsillitis caused by _Streptococcus pyogenes_,  acute exacerbations of chronic bronchitis caused by _Streptococcus pneumoniae_ and _Haemophilus influenzae_, and uncomplicated gonorrhea (cervical/urethral) caused by _Neisseria gonorrhoeae_ (penicillinase-and non-penicillinase-producing isolates).[L49293]', 'The bacterial cell wall, which is located at the periphery of Gram-positive bacteria and within the periplasm of Gram-negative bacteria, comprises a glycopeptide polymer synthesized through cross-linking of glycans to peptide stems on alternating saccharides, which is known commonly as peptidoglycan.[A232920] Cell wall formation, recycling, and remodelling require numerous enzymes, including a family of enzymes with similar active site character despite distinct and sometimes overlapping roles as carboxypeptidases, endopeptidases, transpeptidases, and transglycosylases, known as "penicillin-binding proteins" (PBPs). The number of PBPs differs between bacteria, in which some are considered essential and others redundant. In general, inhibition of one or more essential PBPs results in impaired cell wall homeostasis, loss of cell integrity, and is ultimately bactericidal.[A232920, A232925, A232930]



Cefixime is a cephalosporin and cephalosporins work by using their beta-lactam rings to inhibit bacterial cell wall synthesis by binding to the penicillin-binding proteins transpeptidases on bacteria.[A262995,A262945,A262950] The inhibition of synthesis of the bacteria cell wall will cause lysis, particularly in fast growing organisms such as bacteria.[A262945] Specifically, cephalosporins inhibit penicillin-sensitive enzymes responsible for the final 3D structure of the bacterial cell wall which in turn inhibit bacterial cell wall peptidoglycan synthesis.[T894, T322] ', 'Cefixime, a broad-spectrum antibiotic, is an orally-active third-generation semisynthetic cephalosporin.[A262894,L49293] Like other cephalosporins, the antibacterial action of cefixime results from inhibition of cell wall synthesis.[L49293] Also like other cephalosporins, cefixime is stable when in the presence of certain beta-lactamase enzymes, which means certain organisms resistant to penicillins and some cephalosporins due to the presence of beta-lactamases could be susceptible to cefixime.[L49293]



Use of cefixime can result in hypersensitivity reactions including anaphylactic/anaphylactoid reactions and _Clostridium difficile_-associated diarrhea (CDAD); it may also be associated with a fall in prothrombin activity.[L49293] Cefixime doses should be adjusted for patients that have renal impairment and patients undergoing continuous ambulatory peritoneal dialysis (CAPD) and hemodialysis (HD), while patients on dialysis should be monitored while taking cefixime.[L49293]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (4360, 'Naviluca 200 mg 7 caps in', 'نافيلوكا 200مجم 7 كبسولة', '255', '178', 'Antifungals.triazoles', 'Fluconazole', 'International drug industries > rotabiogen for pharmaceutical invest.', 'Cap', 'كبسولة', '200 mg', '1', NULL, NULL, '6224009665167', NULL, 1496, '2024-06-25', 'Fluconazole can be administered in the treatment of the following fungal infections[L11043]:



 1) Vaginal yeast infections caused by Candida

 2) Systemic Candida infections

 3) Both esophageal and oropharyngeal candidiasis 

 4) Cryptococcal meningitis

 5) UTI (urinary tract infection) by Candida

 6) Peritonitis (inflammation of the peritoneum) caused by Candida



**A note on fungal infection prophylaxis**



Patients receiving bone marrow transplantation who are treated with cytotoxic chemotherapy and/or radiation therapy may be predisposed to candida infections, and may receive fluconazole as prophylactic therapy.[L11043]



**A note on laboratory testing**



Obtaining specimens for fungal culture and other important laboratory studies such as serology or pathology is advised before starting fluconazole therapy in order to isolate the organisms to be eliminated through treatment. It is permissible to start therapy before the results are available, however, adjusting the therapy once laboratory results confirm the causative organism may be necessary.[L11043]

', 'Fluconazole is a very selective inhibitor of fungal cytochrome P450 dependent enzyme _lanosterol 14-α-demethylase_. This enzyme normally works to convert _lanosterol_ to _ergosterol_, which is necessary for fungal cell wall synthesis.  The free nitrogen atom located on the azole ring of fluconazole binds with a single iron atom located in the heme group of lanosterol 14-α-demethylase.[A178774] This prevents oxygen activation and, as a result, inhibits the demethylation of lanosterol, halting the process of ergosterol biosynthesis.[A178777]  Methylated sterols are then found to accumulate in the fungal cellular membrane, leading to an arrest of fungal growth.[A178774] These accumulated sterols negatively affect the structure and function of the fungal cell plasma membrane.[A16632]



Fluconazole resistance may arise from an alteration in the amount or function of the target enzyme (lanosterol 14-α-demethylase), altered access to this enzyme, or a combination of the above.[A16632] Other mechanisms may also be implicated, and studies are ongoing.[A178783]', 'Fluconazole has been demonstrated to show fungistatic activity against the majority of strains of the following microorganisms, curing fungal infections[L11043]:



_Candida albicans, Candida glabrata (Many strains are intermediately susceptible), Candida parapsilosis, Candida tropicalis, Cryptococcus neoformans_



This is achieved through steroidal inhibition in fungal cells, interfering with cell wall synthesis and growth as well as cell adhesion, thereby treating fungal infections and their symptoms.[L11043,A174325,A174343]



The fungistatic activity of fluconazole has also been shown in normal and immunocompromised animal models with both systemic and intracranial fungal infections caused by _Cryptococcus neoformans_ and for systemic infections caused by Candida albicans.[L11043]  It is important to note that resistant organisms have been found against various strains of organisms treated with fluconazole.[A178759,A178762,A178765] This further substantiates the need to perform susceptibility testing when fluconazole is considered as an antifungal therapy.[A178768,A178786] 





**A note on steroidal effects of fluconazole**



There has been some concern that fluconazole may interfere with and inactivate human steroids/hormones due to the inhibition of hepatic cytochrome enzymes.[L11034]  Fluconazole has demonstrated to be more selective for _fungal_ cytochrome P-450 enzymes than for a variety of mammalian cytochrome P-450 enzymes. Fluconazole 50 mg administered daily for up to 28 days in individuals of reproductive age has been show to have no effect on testosterone plasma concentrations of males and plasma concentrations of steroids in females. A 200-400 mg dose of fluconazole showed no clinically relevant effect on steroid levels or on ACTH-stimulated steroid response in healthy males, in one clinical study mentioned on the European Medicines Agency label.[L11034] Other studies have shown no significant effects of fluconazole on steroid levels, further confirming these data.[A174358,A178780]', 'DrugBank', 0, 0, 0);
INSERT OR IGNORE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction) VALUES (19837, 'Glimepir 3mg 10 tab.', 'جليميبر 3 مجم 10 اقراص', '4', NULL, 'Anti-diabetic.secretagogues.sulfonylurea', 'Glimepiride', 'Biopharm egypt', 'Tab', 'أقراص', '3mg', '1', NULL, 'indications : this medication is a sulfonylurea antidiabetic agent prescribed for type 2 diabetes. it works by causing the pancreas to produce insulin dosage : the recommended starting dose is 1-2 mg once daily with food.maximum: 2mg/day contraindications', NULL, NULL, 1163, '2022-07-09', 'Glimepiride is indicated for the management of type 2 diabetes in adults as an adjunct to diet and exercise to improve glycemic control as monotherapy. 



It may also be indicated for use in combination with metformin or insulin to lower blood glucose in patients with type 2 diabetes whose high blood sugar levels cannot be controlled by diet and exercise in conjunction with an oral hypoglycemic (a drug used to lower blood sugar levels) agent alone.[L10322]', 'ATP-sensitive potassium channels on pancreatic beta cells that are gated by intracellular ATP and ADP. The hetero-octomeric complex of the channel is composed of four pore-forming Kir6.2 subunits and four regulatory sulfonylurea receptor (SUR) subunits. Alternative splicing allows the formation of channels composed of varying subunit isoforms expressed at different concentrations in different tissues.[A177730] In pancreatic beta cells, ATP-sensitive potassium channels play a role as essential metabolic sensors and regulators that couple membrane excitability with glucose-stimulated insulin secretion (GSIS).[A177727] When there is a decrease in the ATP:ADP ratio, the channels are activated and open, leading to K+ efflux from the cell, membrane hyperpolarization, and suppression of insulin secretion.[A177727] In contrast, increased uptake of glucose into the cell leads to elevated intracellular ATP:ADP ratio, leading to the closure of channels and membrane depolarization. Depolarization leads to activation and opening of the voltage-dependent Ca2+ channels and consequently an influx of calcium ions into the cell.[A177727] Elevated intracellular calcium levels causes the contraction of the filaments of actomyosin responsible for the exocytosis of insulin granules stored in vesicles.[A177715] Glimepiride blocks the ATP-sensitive potassium channel by binding non-specifically to the B sites of both sulfonylurea receptor-1 (SUR1) and sulfonylurea receptor-2A (SUR2A) subunits as well as the A site of SUR1 subunit of the channel to promote insulin secretion from the beta cell.[A177715] ', 'Glimepiride stimulates the secretion of insulin granules from the pancreatic beta cells and improves the sensitivity of peripheral tissues to insulin to increase peripheral glucose uptake, thus reducing plasma blood glucose levels and glycated hemoglobin (HbA1C) levels. A multi-center, randomized, placebo-controlled clinical trial evaluated the efficacy of glimepiride (1–8 mg) as monotherapy titrated over 10 weeks compared with placebo in T2DM subjects who were not controlled by diet alone.[A177703] In this study, there was a reduction in fasting plasma glucose (FPG) by 46 mg/dL, post-prandial glucose (PPG) by 72 mg/dL, and HbA1c by 1.4% more than the placebo.[A177703] In another randomized study comprising of patients with T2DM receiving either placebo or one of the three doses (1, 4, or 8 mg) of glimepiride during a 14-week study period, all glimepiride regimens significantly reduced FPG, PPG, and HbA1c values (P < 0.001) compared to placebo by the end of the study period.[A177703] The 4- and 8-mg doses of glimepiride were more effective than the 1-mg dose; however, the 4-mg dose provided a nearly maximal antihyperglycemic effect.[A177703]', 'DrugBank', 0, 0, 0);
